	forest_file_id varchar(10),
	timber_mark varchar(10),
	cut_block_id varchar(10),
	cutting_permit_id varchar(3),
	disturbance_gross_area decimal(11,4),
	disturbance_start_date timestamp(0),
	disturbance_end_date timestamp(0),
	opening_id bigint,
	opening_gross_area decimal(11,4),
	planned_harvest_date timestamp(0),
	planned_gross_block_area decimal(11,4),
	planned_net_block_area decimal(11,4),
	opening_prime_licence_ind varchar(1),
	cb_skey bigint,
	revision_count integer NOT NULL,
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON TABLE cut_block_open_admin IS E'Used by FTA and RESULTS to track area and date information associated with a cut block and/or opening.';
COMMENT ON COLUMN cut_block_open_admin.cb_skey IS E'The unique identifer for a cut block.';
COMMENT ON COLUMN cut_block_open_admin.cut_block_id IS E'Identifier for a cut block of a harvesting tenure (within a cutting permit for tenures with cp"s).';
COMMENT ON COLUMN cut_block_open_admin.cut_block_open_admin_id IS E'Primary Unique identifier for a Cut Block Open Admin.';
COMMENT ON COLUMN cut_block_open_admin.cutting_permit_id IS E'Identifier for a cutting permit associated with a quota type tenure.';
COMMENT ON COLUMN cut_block_open_admin.disturbance_end_date IS E'For areas being Harvested, this date is associated with the actual or estimated completion of Harvesting. This date s/b entered as part of the regular logging inspection, and updated as required. The actual date will be when the status moves to S - Silv.';
COMMENT ON COLUMN cut_block_open_admin.disturbance_gross_area IS E'Current gross area of the cut block, in hectares, including all approved amendments.';
COMMENT ON COLUMN cut_block_open_admin.disturbance_start_date IS E'Actual date that harvesting started on the cut block.';
COMMENT ON COLUMN cut_block_open_admin.entry_timestamp IS E'Timestamp when the event information was entered.';
COMMENT ON COLUMN cut_block_open_admin.entry_userid IS E'The unique user id of the resource who initially added the entry.';
COMMENT ON COLUMN cut_block_open_admin.forest_file_id IS E'File identification assigned to Provincial Forest Use files. Assigned file number. Usually the Licence, Tenure or Private Mark number.';
COMMENT ON COLUMN cut_block_open_admin.opening_gross_area IS E'The total area that the opening encompasses (e.g., 120 Hectares).';
COMMENT ON COLUMN cut_block_open_admin.opening_id IS E'System generated value uniquely identifying the opening.';
COMMENT ON COLUMN cut_block_open_admin.opening_prime_licence_ind IS E'A (Y/N) indicator used to show whether or not this is the primary licence holder for the Opening.';
COMMENT ON COLUMN cut_block_open_admin.planned_gross_block_area IS E'Estimated initial gross area of the cut block, in hectares, from the PHSP.';
COMMENT ON COLUMN cut_block_open_admin.planned_harvest_date IS E'The date on which the harvest was planned.';
COMMENT ON COLUMN cut_block_open_admin.planned_net_block_area IS E'Estimated planned gross area of the cut block, in hectares, from the PHSP.';
COMMENT ON COLUMN cut_block_open_admin.revision_count IS E'A count of the number of times an entry in the entity has been modified. Used to validate if the current information displayed on a user"s web browser is the most current.';
COMMENT ON COLUMN cut_block_open_admin.timber_mark IS E'Unique identifying set of characters to be stamped or marked on the end of each log to associate the log with the specific authority to harvest and move timber.';
COMMENT ON COLUMN cut_block_open_admin.update_timestamp IS E'The date and time the entry was last modified.';
COMMENT ON COLUMN cut_block_open_admin.update_userid IS E'The unique id of the user who last modified this entry.';


CREATE TABLE cut_regulation_code (
	cut_regulation_code varchar(3) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON COLUMN cut_regulation_code.cut_regulation_code IS E'Identifies the type of regulation applicable to a given cut block. Valid values include "Bark Beetle Regulation", and "Small Scale Salvage".';
COMMENT ON COLUMN cut_regulation_code.description IS E'Description of the code value';
COMMENT ON COLUMN cut_regulation_code.effective_date IS E'Date the code becomes effective';
COMMENT ON COLUMN cut_regulation_code.expiry_date IS E'Date the code expires';
COMMENT ON COLUMN cut_regulation_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE data_source_code (
	data_source_code varchar(10) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON COLUMN data_source_code.data_source_code IS E'Describes the origin of a feature image. E.g. Air Photo, Forest Cover Maps, Satelite.';
COMMENT ON COLUMN data_source_code.description IS E'Description of the code value';
COMMENT ON COLUMN data_source_code.effective_date IS E'Date the code becomes effective';
COMMENT ON COLUMN data_source_code.expiry_date IS E'Date the code expires';
COMMENT ON COLUMN data_source_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE disturbance_code (
	disturbance_code varchar(3) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON COLUMN disturbance_code.description IS E'Full description of code value.';
COMMENT ON COLUMN disturbance_code.disturbance_code IS E'A code indicating the reason for the disturbance within the opening.';
COMMENT ON COLUMN disturbance_code.effective_date IS E'The date the value is available for use.';
COMMENT ON COLUMN disturbance_code.expiry_date IS E'The date the value is no longer available for use.';
COMMENT ON COLUMN disturbance_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE feature_classes (
	feature_class_skey bigint NOT NULL,
	group_name_cd varchar(10) NOT NULL,
	name varchar(60) NOT NULL,
	feature_type_cd varchar(10) NOT NULL,
	data_maturity_cd char(1) NOT NULL,
	overlapping_feature_ind char(1) NOT NULL,
	span_tile_ind char(1) NOT NULL,
	derived_data_ind char(1) NOT NULL,
	mandatory_metadata_ind char(1) NOT NULL,
	custodian_org_unit_cd varchar(6),
	data_standards_manager varchar(60),
	steward_org_unit_cd varchar(6),
	extended_org_unit_cd varchar(6),
	extended_data_contact varchar(60),
	discipline_standard_cd varchar(4),
	collection_process varchar(255),
	entry_timestamp timestamp(0) NOT NULL,
	entry_userid varchar(32) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	update_userid varchar(32) NOT NULL,
	description varchar(2000) NOT NULL,
	comments varchar(2000)
) ;
COMMENT ON COLUMN feature_classes.feature_class_skey IS E'The unique key assigned to a Feature Class by the Ministry of Forests.';


CREATE TABLE file_client_type_code (
	file_client_type_code varchar(1) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON TABLE file_client_type_code IS E'Identifes the client types codes applicable for assignment to forest files.';
COMMENT ON COLUMN file_client_type_code.description IS E'Description of the code value';
COMMENT ON COLUMN file_client_type_code.effective_date IS E'Date the code becomes effective';
COMMENT ON COLUMN file_client_type_code.expiry_date IS E'Date the code expires';
COMMENT ON COLUMN file_client_type_code.file_client_type_code IS E'Identifes the client types codes applicable for assignment to forest files.';
COMMENT ON COLUMN file_client_type_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE file_type_code (
	file_type_code varchar(10) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON COLUMN file_type_code.description IS E'Description of the code value';
COMMENT ON COLUMN file_type_code.effective_date IS E'Date the code becomes effective';
COMMENT ON COLUMN file_type_code.expiry_date IS E'Date the code expires';
COMMENT ON COLUMN file_type_code.file_type_code IS E'The FTAS code to indicate the type of file, and often synonymous with a tenure or a project.';
COMMENT ON COLUMN file_type_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE fire_harvesting_reason_code (
	fire_harvesting_reason_code varchar(10) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON TABLE fire_harvesting_reason_code IS E'A code or abbreviated text uniquely identifies a reason for harvesting related to fire activities (prevention, clean up)';
COMMENT ON COLUMN fire_harvesting_reason_code.fire_harvesting_reason_code IS E'FIRE_HARVESTING_REASON_CODE is a code that identifies a reason for harvesting related to fire activities (prevention, clean up).';


CREATE TABLE forest_client (
	client_number varchar(8) NOT NULL,
	client_name varchar(60) NOT NULL,
	legal_first_name varchar(30),
	legal_middle_name varchar(30),
	client_status_code varchar(3) NOT NULL,
	client_type_code varchar(1) NOT NULL,
	birthdate timestamp(0),
	client_id_type_code varchar(4),
	client_identification varchar(40),
	registry_company_type_code varchar(4),
	corp_regn_nmbr varchar(9),
	client_acronym varchar(8),
	wcb_firm_number varchar(6),
	ocg_supplier_nmbr varchar(10),
	client_comment varchar(4000),
	add_timestamp timestamp(0) NOT NULL,
	add_userid varchar(30) NOT NULL,
	add_org_unit bigint NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_org_unit bigint NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON TABLE forest_client IS E'Any individual who is dealing, has dealt or plans to deal with the Ministry.';
COMMENT ON COLUMN forest_client.add_org_unit IS E'Unique physical identifier for the storage of organizational unit codes for the Ministry of Forests" offices. Values stored here are for the computer"s use only, and are not to be used by people as "ministry codes".';
COMMENT ON COLUMN forest_client.add_timestamp IS E'The date and time on which the entry was created.';
COMMENT ON COLUMN forest_client.add_userid IS E'The USERID of the individual performing the entry.';
COMMENT ON COLUMN forest_client.birthdate IS E'Client"s date of birth, if the client is an individual.';
COMMENT ON COLUMN forest_client.client_acronym IS E'A familiar aphabetic acronym to be used as an alternate to the ministry"s client number for data entry and display.';
COMMENT ON COLUMN forest_client.client_comment IS E'Text for the comment on the client.';
COMMENT ON COLUMN forest_client.client_id_type_code IS E'A code indicating a type of identification used to identify a Client. Eg. BC Drivers Licence, Military Number, SIN';
COMMENT ON COLUMN forest_client.client_identification IS E'The reference number for sources of identification for an Individual client.';
COMMENT ON COLUMN forest_client.client_name IS E'The name of the Ministry Client - Company or Individual. Entered as: the full corporate name if a Corporation; the full registered name if a Partnership; the legal surname if an Individual.';
COMMENT ON COLUMN forest_client.client_number IS E'Sequentially assigned number to identify a ministry client.';
COMMENT ON COLUMN forest_client.client_status_code IS E'A code indicating the status of ministry client.';
COMMENT ON COLUMN forest_client.client_type_code IS E'A code indicating a type of ministry client.';
COMMENT ON COLUMN forest_client.corp_regn_nmbr IS E'Client"s corporate registration number.';
COMMENT ON COLUMN forest_client.legal_first_name IS E'The client"s legal first name';
COMMENT ON COLUMN forest_client.legal_middle_name IS E'The client"s legal middle name of names if an individual.';
COMMENT ON COLUMN forest_client.ocg_supplier_nmbr IS E'Office of the Controller General"s reference number for identification of Government Suppliers.';
COMMENT ON COLUMN forest_client.registry_company_type_code IS E'A code indicating a type of Corporate Registry company.';
COMMENT ON COLUMN forest_client.revision_count IS E'Number of times updated';
COMMENT ON COLUMN forest_client.update_org_unit IS E'Unique physical identifier for the storage of organizational unit codes for the Ministry of Forests" offices. Values stored here are for the computer"s use only, and are not to be used by people as "ministry codes".';
COMMENT ON COLUMN forest_client.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN forest_client.update_userid IS E'The USERID of the individual who last updated this information.';
COMMENT ON COLUMN forest_client.wcb_firm_number IS E'Workers Compensation Board registration number.';


CREATE TABLE forest_cover (
	forest_cover_id bigint NOT NULL,
	opening_id bigint NOT NULL,
	stocking_standard_unit_id bigint,
	silv_polygon_no varchar(30) NOT NULL,
	silv_polygon_area decimal(7,1) NOT NULL,
	silv_polygon_net_area decimal(7,1) NOT NULL,
	stocking_class_code varchar(1),
	stocking_status_code varchar(3),
	stocking_type_code varchar(3),
	reference_year smallint NOT NULL,
	reentry_year smallint,
	site_class_code varchar(1),
	site_index integer,
	site_index_source_code varchar(1),
	silv_reserve_code varchar(1),
	silv_reserve_objective_code varchar(3),
	tree_species_code varchar(8),
	tree_cover_pattern_code varchar(1),
	results_submission_id bigint,
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON TABLE forest_cover IS E'Describes the opening"s forest cover attributes stratified by Forest Cover Polygon which will satisfy Inventory Program requirements.';
COMMENT ON COLUMN forest_cover.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN forest_cover.entry_userid IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN forest_cover.forest_cover_id IS E'System generated value uniquely identifying the forest cover.';
COMMENT ON COLUMN forest_cover.opening_id IS E'System generated value uniquely identifying the opening.';
COMMENT ON COLUMN forest_cover.reentry_year IS E'The time to the next harvest (i.e. the next harvest year).';
COMMENT ON COLUMN forest_cover.reference_year IS E'The year age and height measurements were recorded.';
COMMENT ON COLUMN forest_cover.results_submission_id IS E'Used to link information about a data submission. A submission may have many rows of Silviculture (Form A), Activities (Form B), and Forest Cover/Milestones (Form C) information. This attribute will be receiving a system generated number from the Electronic Submission Framework.';
COMMENT ON COLUMN forest_cover.revision_count IS E'A counter identifying the number of times this record as been modified. Used in the web page access to determine if the record as been modified since the data was first retrieved.';
COMMENT ON COLUMN forest_cover.silv_polygon_area IS E'The total area occupied by each polygon number or stratified land type. The sum of all areas for a polygon(s) should not be greater than the area of the SU.';
COMMENT ON COLUMN forest_cover.silv_polygon_net_area IS E'Net Area of the Polygon (i.e. SILV POLYGON AREA less non-mapped area (FOREST COVER NON MAPPED AREA))';
COMMENT ON COLUMN forest_cover.silv_polygon_no IS E'An unique number identification derived from the IGDS which is assigned to each forest type island, standards unit, eco-strata or survey strata within the opening.';
COMMENT ON COLUMN forest_cover.silv_reserve_code IS E'A code for the type of reserve. Values are group (b), uniform (u) or variable (v).';
COMMENT ON COLUMN forest_cover.silv_reserve_objective_code IS E'A code used to define the primary reserve objective. ( e.g. Riparian, Wildlife Habitat) .';
COMMENT ON COLUMN forest_cover.site_class_code IS E'This code indicates the quality of the land. Note: This column is not maintained by the system.';
COMMENT ON COLUMN forest_cover.site_index IS E'The site index for the primary tree species in the previous stand (for the area under the prescription).';
COMMENT ON COLUMN forest_cover.site_index_source_code IS E'A code describing the source, or origin, of the estimated site index.';
COMMENT ON COLUMN forest_cover.stocking_class_code IS E'The stocking class code for the primary tree species in the previous stand (for the area under the prescription).';
COMMENT ON COLUMN forest_cover.stocking_standard_unit_id IS E'System generated value uniquely identifing the stocking standards.';
COMMENT ON COLUMN forest_cover.stocking_status_code IS E'A code to describe the previous stand type for the area under the prescription.';
COMMENT ON COLUMN forest_cover.stocking_type_code IS E'A further classification of the stocking status for the polygon.';
COMMENT ON COLUMN forest_cover.tree_cover_pattern_code IS E'The dispursion of trees over the area.';
COMMENT ON COLUMN forest_cover.tree_species_code IS E'The secondary tree species of Crown timber that was cut, damaged, destroyed or removed without authority';
COMMENT ON COLUMN forest_cover.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN forest_cover.update_userid IS E'The USERID of the individual who last updated the information.';


