COMMENT ON COLUMN forest_cover_archive.revision_count IS E'A counter identifying the number of times this record as been modified. Used in the web page access to determine if the record as been modified since the data was first retrieved.';
COMMENT ON COLUMN forest_cover_archive.silv_polygon_area IS E'The total area occupied by each polygon number or stratified land type. The sum of all areas for a polygon(s) should not be greater than the area of the SU.';
COMMENT ON COLUMN forest_cover_archive.silv_polygon_net_area IS E'Net Area of the Polygon (i.e. SILV POLYGON AREA less non-mapped area (FOREST COVER NON MAPPED AREA))';
COMMENT ON COLUMN forest_cover_archive.silv_polygon_no IS E'An unique number identification derived from the IGDS which is assigned to each forest type island, standards unit, eco-strata or survey strata within the opening.';
COMMENT ON COLUMN forest_cover_archive.silv_reserve_code IS E'A code for the type of reserve. Values are group (b), uniform (u) or variable (v).';
COMMENT ON COLUMN forest_cover_archive.silv_reserve_objective_code IS E'A code used to define the primary reserve objective. ( e.g. Riparian, Wildlife Habitat) .';
COMMENT ON COLUMN forest_cover_archive.site_class_code IS E'This code indicates the quality of the land. Note: This column is not maintained by the system.';
COMMENT ON COLUMN forest_cover_archive.site_index IS E'The site index for the primary tree species in the previous stand (for the area under the prescription).';
COMMENT ON COLUMN forest_cover_archive.site_index_source_code IS E'A code describing the source, or origin, of the estimated site index.';
COMMENT ON COLUMN forest_cover_archive.stocking_class_code IS E'The stocking class code for the primary tree species in the previous stand (for the area under the prescription).';
COMMENT ON COLUMN forest_cover_archive.stocking_standard_unit_id IS E'System generated value uniquely identifing the stocking standards.';
COMMENT ON COLUMN forest_cover_archive.stocking_status_code IS E'A code to describe the previous stand type for the area under the prescription.';
COMMENT ON COLUMN forest_cover_archive.stocking_type_code IS E'A further classification of the stocking status for the polygon.';
COMMENT ON COLUMN forest_cover_archive.tree_cover_pattern_code IS E'The dispursion of trees over the area.';
COMMENT ON COLUMN forest_cover_archive.tree_species_code IS E'The secondary tree species of Crown timber that was cut, damaged, destroyed or removed without authority';
COMMENT ON COLUMN forest_cover_archive.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN forest_cover_archive.update_userid IS E'The USERID of the individual who last updated the information.';


CREATE TABLE forest_cover_archive_geometry (
	forest_cover_id bigint NOT NULL,
	archive_date timestamp(0) NOT NULL,
	geometry geometry(GEOMETRY,3005) NOT NULL,
	feature_area decimal(11,4) NOT NULL,
	feature_perimeter decimal(11,4) NOT NULL,
	feature_class_skey bigint NOT NULL,
	data_source_code varchar(10),
	capture_method_code varchar(30),
	observation_date timestamp(0),
	data_quality_comment varchar(255),
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON TABLE forest_cover_archive_geometry IS E'The spatial polygon component for Forest Covers which has been modified.';
COMMENT ON COLUMN forest_cover_archive_geometry.archive_date IS E'The date the forest cover entry was archived to this entity.';
COMMENT ON COLUMN forest_cover_archive_geometry.data_quality_comment IS E'Allows users to specify comments regarding the accuracy and general quality of the given feature"s data.';
COMMENT ON COLUMN forest_cover_archive_geometry.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN forest_cover_archive_geometry.entry_userid IS E'The userid of the individual who entered the information.';
COMMENT ON COLUMN forest_cover_archive_geometry.feature_area IS E'The area of the polygon pertaining to the Forest Cover measured in hectares.';
COMMENT ON COLUMN forest_cover_archive_geometry.feature_class_skey IS E'Unique identifier of the feature class.';
COMMENT ON COLUMN forest_cover_archive_geometry.feature_perimeter IS E'The perimeter of the polygon pertaining to the Forest Cover measured in metres.';
COMMENT ON COLUMN forest_cover_archive_geometry.forest_cover_id IS E'System generated value uniquely identifying the forest cover.';
COMMENT ON COLUMN forest_cover_archive_geometry.geometry IS E'Spatial coordinate representation of the polygon related to the Forest Cover.';
COMMENT ON COLUMN forest_cover_archive_geometry.observation_date IS E'Captures the date of observation for the given feature image.';
COMMENT ON COLUMN forest_cover_archive_geometry.revision_count IS E'A counter identifying the number of times this record as been modified. Used in the web page access to determine if the record as been modified since the data was first retrieved.';
COMMENT ON COLUMN forest_cover_archive_geometry.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN forest_cover_archive_geometry.update_userid IS E'The userid of the individual who last updated the information.';


