CREATE TABLE forest_stewardship_plan (
	fsp_id bigint NOT NULL,
	fsp_amendment_number bigint NOT NULL,
	fsp_status_code varchar(4) NOT NULL,
	fsp_amendment_code varchar(4) NOT NULL DEFAULT 'ORG',
	licensee_amendment_name varchar(30),
	plan_name varchar(120) NOT NULL,
	plan_term_years smallint,
	plan_term_months smallint,
	plan_start_date timestamp(0),
	plan_end_date timestamp(0),
	plan_submission_date timestamp(0) NOT NULL,
	plan_decision_date timestamp(0),
	plan_decision_userid varchar(30),
	transition_ind varchar(1) NOT NULL DEFAULT 'N',
	identified_areas_update_ind varchar(1) NOT NULL DEFAULT 'N',
	fdu_update_ind varchar(1) NOT NULL DEFAULT 'N',
	stocking_standard_update_ind varchar(1) NOT NULL DEFAULT 'N',
	frpa197_election_ind varchar(1) NOT NULL DEFAULT 'N',
	contact_name varchar(120) NOT NULL,
	telephone_number varchar(10) NOT NULL,
	email_address varchar(120) NOT NULL,
	amendment_approval_date timestamp(0),
	amendment_authority varchar(100),
	amendment_approval_requird_ind varchar(1),
	amendment_submission_date timestamp(0),
	revision_count integer NOT NULL,
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON TABLE forest_stewardship_plan IS E'A plan submitted by a forest industry licensee stating how the BC Government?s objectives for managing the province?s forest resources will be met. It identifies the plan-holder?s obligations for a five-year period.';
COMMENT ON COLUMN forest_stewardship_plan.amendment_approval_date IS E'Date amendment to plan approved by MoFR';
COMMENT ON COLUMN forest_stewardship_plan.amendment_approval_requird_ind IS E'Indicates if the amendment needs MoFR approval';
COMMENT ON COLUMN forest_stewardship_plan.amendment_authority IS E'Identifier of MoFR decision maker';
COMMENT ON COLUMN forest_stewardship_plan.amendment_submission_date IS E'Date amendment to plan submitted by plan holder';
COMMENT ON COLUMN forest_stewardship_plan.contact_name IS E'Name of a contact for the submitting licensee.';
COMMENT ON COLUMN forest_stewardship_plan.email_address IS E'Email of contact for submitting licensee';
COMMENT ON COLUMN forest_stewardship_plan.entry_timestamp IS E'Contains the system timestamp when data in a table was inserted.';
COMMENT ON COLUMN forest_stewardship_plan.entry_userid IS E'The userid responsible for inserting data into a table.';
COMMENT ON COLUMN forest_stewardship_plan.fdu_update_ind IS E'Indicates when FDU data will be changed as part of the amendment.';
COMMENT ON COLUMN forest_stewardship_plan.frpa197_election_ind IS E'Indicates whether a licensee has elected (under the Forest and Range Practices Act section 197 legislation) to roll over existing cut blocks that were created under the authority of the Forest Practices Code Act. By rolling these cut blocks over, the licensee indicates that the blocks will be governed under the Stocking Standards identified within their approved Forest Stewardship Plan.';
COMMENT ON COLUMN forest_stewardship_plan.fsp_amendment_code IS E'A code indicating the type of amendment to an FSP. Examples include Original, Amendment and Replacement';
COMMENT ON COLUMN forest_stewardship_plan.fsp_amendment_number IS E'Identifies amendment level of FSP';
COMMENT ON COLUMN forest_stewardship_plan.fsp_id IS E'Unique identifier for Forest Stewardship Plan';
COMMENT ON COLUMN forest_stewardship_plan.fsp_status_code IS E'A code indicating the status of the Forest Stewardship Plan. Examples include but are not limited to SUB (submitted), REJ (rejected) and APP (approved).';
COMMENT ON COLUMN forest_stewardship_plan.identified_areas_update_ind IS E'Indicates when Declared Areas data will be changed as part of the amendment.';
COMMENT ON COLUMN forest_stewardship_plan.licensee_amendment_name IS E'The licensee-specified name given to the amendment.';
COMMENT ON COLUMN forest_stewardship_plan.plan_decision_date IS E'The date on which the original FSP or amendment is approved or rejected.';
COMMENT ON COLUMN forest_stewardship_plan.plan_decision_userid IS E'The user that approved or rejected the FSP or amendment.';
COMMENT ON COLUMN forest_stewardship_plan.plan_end_date IS E'The date the FSP expires. The PLAN END DATE is calculated using either the Term Year/Months entered by the user or the End date.';
COMMENT ON COLUMN forest_stewardship_plan.plan_name IS E'Textual name of FSP, provided by plan holder';
COMMENT ON COLUMN forest_stewardship_plan.plan_start_date IS E'The date the FSP comes into legal effect. Set by the FSP Decision Maker.';
COMMENT ON COLUMN forest_stewardship_plan.plan_submission_date IS E'Date FSP was submitted to MoFR';
COMMENT ON COLUMN forest_stewardship_plan.plan_term_months IS E'Length of FSP term in months beyond the indicated term in years (PLAN_TERM_YEARS).';
COMMENT ON COLUMN forest_stewardship_plan.plan_term_years IS E'Length of FSP term in years';
COMMENT ON COLUMN forest_stewardship_plan.revision_count IS E'A count of the number of times an entry in the entity has been modified. Used to validate if the current information displayed on a user"s web browser is the most current.';
COMMENT ON COLUMN forest_stewardship_plan.stocking_standard_update_ind IS E'Indicates when Stocking Standard data will be changed as part of the amendment.';
COMMENT ON COLUMN forest_stewardship_plan.telephone_number IS E'The business telephone number including area code of the contact for submitting licensee';
COMMENT ON COLUMN forest_stewardship_plan.transition_ind IS E'Indicates when the FSP is transitional. A transitional FSP is an original submission created from an existing FDP that does not require approval.';
COMMENT ON COLUMN forest_stewardship_plan.update_timestamp IS E'The timestamp of the last update to the road section record.';
COMMENT ON COLUMN forest_stewardship_plan.update_userid IS E'The userid of the individual who last updated this road section record.';


