COMMENT ON COLUMN forhealth_rslt_archive.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN forhealth_rslt_archive.entry_userid IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN forhealth_rslt_archive.forest_cover_archive_date IS E'The date the forest cover entry was archived to this entity.';
COMMENT ON COLUMN forhealth_rslt_archive.forest_cover_id IS E'System generated value uniquely identifying the forest cover.';
COMMENT ON COLUMN forhealth_rslt_archive.forest_cover_layer_id IS E'System generated value uniquely identifying the forest cover layer.';
COMMENT ON COLUMN forhealth_rslt_archive.forhealth_rslt_id IS E'System generated value uniquely identifying the forest health damage agent occurrance.';
COMMENT ON COLUMN forhealth_rslt_archive.incidence_area IS E'The area in hectares of the region involved.';
COMMENT ON COLUMN forhealth_rslt_archive.incidence_pct IS E'The percentage of the Standards Unit or Opening that the agent is affecting as defined by the survey strata.';
COMMENT ON COLUMN forhealth_rslt_archive.observation_date IS E'The date this observation was made.';
COMMENT ON COLUMN forhealth_rslt_archive.opening_id IS E'System generated value uniquely identifying the opening.';
COMMENT ON COLUMN forhealth_rslt_archive.revision_count IS E'Internal counter used by the system to avoid conflicting updates to the record.';
COMMENT ON COLUMN forhealth_rslt_archive.silv_damage_agent_code IS E'A code indicating the category of insect or disease that has caused a disturbance in the stands history. Stands with a disturbance caused by insects or disease can be further described by the ACTIVITY SUB CODE. The code for the damage agent as it is currently collected (94-3). The FIP based version of Damage Agent code, retained on an as is basis until migration to a stable Ministry version of Damage Agent is availible.';
COMMENT ON COLUMN forhealth_rslt_archive.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN forhealth_rslt_archive.update_userid IS E'The USERID of the individual who last updated the information.';


CREATE TABLE for_client_link (
	for_client_link_skey bigint NOT NULL,
	forest_file_id varchar(10) NOT NULL,
	cutting_permit_id varchar(3),
	cut_block_id varchar(10),
	file_client_type varchar(1) NOT NULL,
	client_number varchar(8) NOT NULL,
	client_locn_code varchar(2) NOT NULL,
	licensee_start_dt timestamp(0),
	licensee_end_date timestamp(0),
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON TABLE for_client_link IS E'Retrofitted from table FOR_CLIENT_LINK';
COMMENT ON COLUMN for_client_link.client_locn_code IS E'A code to uniquely identify, within each client, the addresses of different divisions or locations at which the client operates. The location code is sequentially assigned starting with "00" for the client"s permanent address.';
COMMENT ON COLUMN for_client_link.client_number IS E'Any company or individual who is dealing, has dealt or plans to deal with the Ministry. This is effectively the name and address list for the Ministry.';
COMMENT ON COLUMN for_client_link.cut_block_id IS E'Identifier for a cut block of a harvesting tenure (within a cutting permit for tenures with cp"s).';
COMMENT ON COLUMN for_client_link.cutting_permit_id IS E'Identifier for a cutting permit associated with a quota type tenure.';
COMMENT ON COLUMN for_client_link.entry_timestamp IS E'Timestamp when the event information was entered.';
COMMENT ON COLUMN for_client_link.entry_userid IS E'The unique user id of the resource who initially added the entry.';
COMMENT ON COLUMN for_client_link.file_client_type IS E'Indicates the type of relationship the client has to the file.';
COMMENT ON COLUMN for_client_link.for_client_link_skey IS E'Sequentially generated primary key';
COMMENT ON COLUMN for_client_link.forest_file_id IS E'File identification assigned to Provincial Forest Use files. Assigned file number. Usually the Licence, Tenure or Private Mark number.';
COMMENT ON COLUMN for_client_link.licensee_end_date IS E'The date the licensee stopped being a client';
COMMENT ON COLUMN for_client_link.licensee_start_dt IS E'The date the licensee started being a client.';
COMMENT ON COLUMN for_client_link.revision_count IS E'A count of the number of times an entry in the entity has been modified. Used to validate if the current information displayed on a user"s web browser is the most current.';
COMMENT ON COLUMN for_client_link.update_timestamp IS E'The date and time the entry was last modified.';
COMMENT ON COLUMN for_client_link.update_userid IS E'The unique id of the user who last modified this entry.';


CREATE TABLE fsp_standards_regime_xref (
	fsp_id bigint NOT NULL,
	fsp_amendment_number bigint NOT NULL,
	standards_regime_id bigint NOT NULL,
	revision_count integer NOT NULL,
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON TABLE fsp_standards_regime_xref IS E'A cross reference entity that allows for the relationship between a Forest Stewardship Plan and a Standards Regime. An FSP may refer to many Standards, and a Standard may be included in many FSPs.';
COMMENT ON COLUMN fsp_standards_regime_xref.entry_timestamp IS E'Contains the system timestamp when data in a table was inserted.';
COMMENT ON COLUMN fsp_standards_regime_xref.entry_userid IS E'The userid responsible for inserting data into a table.';
COMMENT ON COLUMN fsp_standards_regime_xref.fsp_amendment_number IS E'Identifies amendment level of FSP';
COMMENT ON COLUMN fsp_standards_regime_xref.fsp_id IS E'Unique identifier for Forest Stewardship Plan';
COMMENT ON COLUMN fsp_standards_regime_xref.revision_count IS E'A count of the number of times an entry in the entity has been modified. Used to validate if the current information displayed on a user"s web browser is the most current.';
COMMENT ON COLUMN fsp_standards_regime_xref.standards_regime_id IS E'Unique identifier for the STANDARD REGIME entry.';
COMMENT ON COLUMN fsp_standards_regime_xref.update_timestamp IS E'The timestamp of the last update to the record.';
COMMENT ON COLUMN fsp_standards_regime_xref.update_userid IS E'The userid of the individual who last updated the record.';


CREATE TABLE fsp_status_code (
	fsp_status_code varchar(4) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON COLUMN fsp_status_code.description IS E'Description of the code value';
COMMENT ON COLUMN fsp_status_code.effective_date IS E'Date the code becomes effective';
COMMENT ON COLUMN fsp_status_code.expiry_date IS E'Date the code expires';
COMMENT ON COLUMN fsp_status_code.fsp_status_code IS E'A code indicating the status of the Forest Stewardship Plan. Examples include but are not limited to SUB (submitted), REJ (rejected) and APP (approved).';
COMMENT ON COLUMN fsp_status_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE harvesting_authority (
	hva_skey bigint NOT NULL,
	forest_file_id varchar(10) NOT NULL,
	cutting_permit_id varchar(3),
	harvesting_authority_id varchar(30),
	forest_district bigint NOT NULL,
	district_admn_zone varchar(4),
	geographic_district bigint NOT NULL,
	mgmt_unit_id varchar(4),
	mgmt_unit_type_code varchar(1),
	licence_to_cut_code varchar(2),
	harvest_type_code varchar(1) NOT NULL,
	harvest_auth_status_code varchar(3),
	tenure_term integer,
	status_date timestamp(0),
	issue_date timestamp(0),
	expiry_date timestamp(0),
	extend_date timestamp(0),
	extend_count bigint,
	harvest_auth_extend_reas_code varchar(1),
	quota_type_code varchar(1),
	crown_lands_region_code varchar(1),
	salvage_type_code varchar(3),
	cascade_split_code varchar(1),
	catastrophic_ind varchar(1),
	crown_granted_ind varchar(1) NOT NULL,
	cruise_based_ind varchar(1) NOT NULL,
	deciduous_ind varchar(1) NOT NULL,
	bcaa_folio_number varchar(23),
	location varchar(100),
	higher_level_plan_reference varchar(30),
	harvest_area decimal(11,4),
	retirement_date timestamp(0),
	revision_count integer NOT NULL,
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	is_waste_assessment_required varchar(1) NOT NULL DEFAULT 'U',
	is_cp_extensn_appl_fee_waived varchar(1) NOT NULL DEFAULT 'U',
	is_cp_extension_appl_fee_paid varchar(1) NOT NULL DEFAULT 'U',
	is_within_fibre_recovery_zone varchar(1) NOT NULL DEFAULT 'U',
	harvesting_authority_guid uuid NOT NULL DEFAULT uuid_generate_v4()
) ;
COMMENT ON TABLE harvesting_authority IS E'Information about the timber cutting permission for a timber tenure.';
COMMENT ON COLUMN harvesting_authority.bcaa_folio_number IS E'BC Assessment Authority Folio Number for the land that the cutting permit/Timber Mark pertains to, if applicable, or the LTO number. BCAA Folio Numbers are in the format C15 733 06604.140-1-2; the format for LTO numbers is nnn-nnn-nnn.';
COMMENT ON COLUMN harvesting_authority.cascade_split_code IS E'Code to indicate the administrative split for the timber mark, east or west of the Cascade Mountains, for tracking timber volume information.';
COMMENT ON COLUMN harvesting_authority.catastrophic_ind IS E'Indicates if the damage to the stand of timber is catastrophic. Otherwise, it is endemic.';
COMMENT ON COLUMN harvesting_authority.crown_granted_ind IS E'Indicator to signify whether the land that the timber mark pertains to has a verifiable crown grant document.';
COMMENT ON COLUMN harvesting_authority.crown_lands_region_code IS E'Describes the CROWN LANDS REGION';
COMMENT ON COLUMN harvesting_authority.cruise_based_ind IS E'Indicates whether a cruise was used for billing purposes.';
COMMENT ON COLUMN harvesting_authority.cutting_permit_id IS E'Identifier for a cutting permit associated with a quota type harvesting tenure.';
COMMENT ON COLUMN harvesting_authority.deciduous_ind IS E'Indicates whether the timber mark is primarily for harvesting deciduous species. Otherwise, coniferous.';
COMMENT ON COLUMN harvesting_authority.district_admn_zone IS E'District Admin Zone is a free format field used internally by the districts to group files within a district for reporting purposes. Examples of use are setting the field to a geographic area or to a persons initials. Reports can be pulled off by District Admin Zone to aid work management. This field is not used by all districts.';
COMMENT ON COLUMN harvesting_authority.entry_timestamp IS E'Timestamp when the event information was entered.';
COMMENT ON COLUMN harvesting_authority.entry_userid IS E'The unique user id of the resource who initially added the entry.';
COMMENT ON COLUMN harvesting_authority.expiry_date IS E'Initial expiry date set upon activation of the harvest authorization.';
COMMENT ON COLUMN harvesting_authority.extend_count IS E'Count of the number of extensions to the expiry date that the harvest authorization has had.';
COMMENT ON COLUMN harvesting_authority.extend_date IS E'Date to which the harvest authorization validity has been extended. Current expiry date.';
COMMENT ON COLUMN harvesting_authority.forest_district IS E'Unique physical identifier for the storage of organizational unit codes for the Ministry of Forests offices. Values stored here are for the computers use only, and are not to be used by people as "ministry codes".';
COMMENT ON COLUMN harvesting_authority.forest_file_id IS E'File identification assigned to Provincial Forest Use files. Assigned file number. Usually the Licence, Tenure or Private Mark number.';
COMMENT ON COLUMN harvesting_authority.geographic_district IS E'Unique physical identifier for the storage of organizational unit codes for the Ministry of Forests offices. Values stored here are for the computers use only, and are not to be used by people as "ministry codes".';
COMMENT ON COLUMN harvesting_authority.harvest_area IS E'Identifes the harvest area in hectares for harvesting authorites without spatial.';
COMMENT ON COLUMN harvesting_authority.harvest_auth_extend_reas_code IS E'Code to indicate the reason to extend the life of the timber mark.';
COMMENT ON COLUMN harvesting_authority.harvest_auth_status_code IS E'The after status image of the harvesting authority. The status indicates the point the harvesting authority is at in it"s lifecycle. Examples are: PP - Planning, HA - Harvesting, LC - Logging Complete, and S - Silviculture.';
COMMENT ON COLUMN harvesting_authority.harvest_type_code IS E'Identifies the type of harvesting authority. Valid values include "Green", "Road", "Multi-Mark", and "Fort St. John".';
COMMENT ON COLUMN harvesting_authority.harvesting_authority_guid IS E'Contains Harvesting Authority Global Unique Identifier';
COMMENT ON COLUMN harvesting_authority.harvesting_authority_id IS E'The licensee provided identifier for non-cp related harvesting tenure. E.g. Fort St. John harvesting authority.';
COMMENT ON COLUMN harvesting_authority.higher_level_plan_reference IS E'Indicates the higher level plan reference for the given harvest authority. E.g. For Fort St. John, this could be "Sustainable Forest Management Plan".';
COMMENT ON COLUMN harvesting_authority.hva_skey IS E'Reference from FTA.';
COMMENT ON COLUMN harvesting_authority.is_cp_extension_appl_fee_paid IS E'A value to show if the extension application fee has been paid.N if IS_EXT_APP_FEE_WAIVED = "Y","U" otherwise. Not mandatory on extension';
COMMENT ON COLUMN harvesting_authority.is_cp_extensn_appl_fee_waived IS E'A value that is mandatory on extension to indicate if the Extension application fee has been waived.';
COMMENT ON COLUMN harvesting_authority.is_waste_assessment_required IS E'A value indicating whether a harvesting Authority record requires waste assessment or not.';
COMMENT ON COLUMN harvesting_authority.is_within_fibre_recovery_zone IS E'A value to show whether a Cutting Permit is in Fibre Recovery Zones or not. The valid values include: "Y" it"s within the Fibre Recovery Zones "N" It"s not within the Fibre Recovery Zones "U" Unknown.';
COMMENT ON COLUMN harvesting_authority.issue_date IS E'Date the harvest authorization was issued.';
COMMENT ON COLUMN harvesting_authority.licence_to_cut_code IS E'The purpose or reason that the licence to cut was granted, eg., right of way.';
COMMENT ON COLUMN harvesting_authority.location IS E'Contains the land legal information for the harvest authority. Land legal information indicates where the land is legally located, such as "Lot 3 of DL 343, Plan 3422".';
COMMENT ON COLUMN harvesting_authority.mgmt_unit_id IS E'The administrative management unit identifier defined by the ministry for managing the forest tenure, eg. 41 - Dawson Creek TSA.';
COMMENT ON COLUMN harvesting_authority.mgmt_unit_type_code IS E'The unique identifier for the management unit type.';
COMMENT ON COLUMN harvesting_authority.quota_type_code IS E'A Timber Supply Area will have volumes assigned to one or more partitions, which are in effect different timber type classifications, or geographic locations. Under each partition are the different apportionments. Examples of Partitions are Conventional';
COMMENT ON COLUMN harvesting_authority.retirement_date IS E'The date the harvesting authority was retired from the spatial conflict layer.';
COMMENT ON COLUMN harvesting_authority.revision_count IS E'A count of the number of times an entry in the entity has been modified. Used to validate if the current information displayed on a user"s web browser is the most current.';
COMMENT ON COLUMN harvesting_authority.salvage_type_code IS E'Code describing the salvage type.';
COMMENT ON COLUMN harvesting_authority.status_date IS E'The date that the harvest authorization status was last updated.';
COMMENT ON COLUMN harvesting_authority.tenure_term IS E'Term of the tenure (in months).';
COMMENT ON COLUMN harvesting_authority.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN harvesting_authority.update_userid IS E'The userid of the individual who last updated this information.';


CREATE TABLE harvest_auth_extend_reas_code (
	harvest_auth_extend_reas_code varchar(1) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON COLUMN harvest_auth_extend_reas_code.description IS E'Description of the code';
COMMENT ON COLUMN harvest_auth_extend_reas_code.effective_date IS E'Date the code is effective';
COMMENT ON COLUMN harvest_auth_extend_reas_code.expiry_date IS E'Date the code expires';
COMMENT ON COLUMN harvest_auth_extend_reas_code.harvest_auth_extend_reas_code IS E'Code to indicate the reason to extend the life of the timber mark.';
COMMENT ON COLUMN harvest_auth_extend_reas_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE harvest_auth_status_code (
	harvest_auth_status_code varchar(3) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON COLUMN harvest_auth_status_code.description IS E'Description of the code value';
COMMENT ON COLUMN harvest_auth_status_code.effective_date IS E'Date the code becomes effective';
COMMENT ON COLUMN harvest_auth_status_code.expiry_date IS E'Date the code expires';
COMMENT ON COLUMN harvest_auth_status_code.harvest_auth_status_code IS E'The after status image of the harvesting authority. The status indicates the point the harvesting authority is at in it"s lifecycle. Examples are: PP - Planning, HA - Harvesting, LC - Logging Complete, and S - Silviculture.';
COMMENT ON COLUMN harvest_auth_status_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE harvest_sale (
	forest_file_id varchar(10) NOT NULL,
	sb_fund_ind varchar(1) NOT NULL DEFAULT 'N',
	sale_method_code varchar(1),
	sale_type_cd varchar(2),
	planned_sale_date timestamp(0),
	tender_opening_dt timestamp(0),
	plnd_sb_cat_code varchar(1),
	sold_sb_cat_code varchar(1),
	total_bidders integer,
	lumpsum_bonus_amt decimal(11,2),
	cash_sale_est_vol decimal(13,2),
	cash_sale_tot_dol decimal(11,2),
	payment_method_cd varchar(1),
	salvage_ind varchar(1) NOT NULL DEFAULT 'N',
	sale_volume bigint,
	admin_area_ind varchar(1) NOT NULL DEFAULT 'N',
	minor_facility_ind varchar(1) NOT NULL DEFAULT 'N',
	bcts_org_unit bigint,
	fta_bonus_bid decimal(11,2),
	fta_bonus_offer decimal(11,2),
	revision_count integer NOT NULL,
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON TABLE harvest_sale IS E'Sale information relating to specific tenures, usually let via the competitive bid process. Information on planned sale and actual sale details are required for Small Business tenures.';
COMMENT ON COLUMN harvest_sale.admin_area_ind IS E'Admin Area Indicator indicates whether cash sales are within an administrative area.';
COMMENT ON COLUMN harvest_sale.bcts_org_unit IS E'Unique physical identifier for the storage of organizational unit codes for the Ministry of Forests offices. Values stored here are for the computers use only, and are not to be used by people as "ministry codes".';
COMMENT ON COLUMN harvest_sale.cash_sale_est_vol IS E'The estimated volume in cubic meters sold under a Cash Sale type Timber Mark. This is mandatory for Cash Sales, and the default is zero for non-cash sales.';
COMMENT ON COLUMN harvest_sale.cash_sale_tot_dol IS E'The total dollars received for a sale sold under a Cash Sale type Timber Mark. This is mandatory for cash sales, and defaults to zero for non-cash sales.';
COMMENT ON COLUMN harvest_sale.entry_timestamp IS E'Timestamp when the event information was entered.';
COMMENT ON COLUMN harvest_sale.entry_userid IS E'The unique user id of the resource who initially added the entry.';
COMMENT ON COLUMN harvest_sale.forest_file_id IS E'File identification assigned to Provincial Forest Use files. Assigned file number. Usually the Licence, Tenure or Private Mark number.';
COMMENT ON COLUMN harvest_sale.fta_bonus_bid IS E'The FTA BONUS BID is the amount paid out as a bonus for the award of the tenure.';
COMMENT ON COLUMN harvest_sale.fta_bonus_offer IS E'The FTA BONUS OFFER is the amount offered as a bonus for the award of the tenure.';
COMMENT ON COLUMN harvest_sale.lumpsum_bonus_amt IS E'Lump sum bonus amount for securing harvesting rights on ministry controlled land.';
COMMENT ON COLUMN harvest_sale.minor_facility_ind IS E'Indicates whether a minor processing facility is on site with an active permit.';
COMMENT ON COLUMN harvest_sale.payment_method_cd IS E'The method of collecting the payment for the licence, such as "Cash" or "Automatic Invoice".';
COMMENT ON COLUMN harvest_sale.planned_sale_date IS E'Estimated sale date of tenure.';
COMMENT ON COLUMN harvest_sale.plnd_sb_cat_code IS E'Describes the type of timber sale.';
COMMENT ON COLUMN harvest_sale.revision_count IS E'A count of the number of times an entry in the entity has been modified. Used to validate if the current information displayed on a user"s web browser is the most current.';
COMMENT ON COLUMN harvest_sale.sale_method_code IS E'Method for type of sale of timber under a tenure. Default: Sealed Tender.';
COMMENT ON COLUMN harvest_sale.sale_type_cd IS E'Describes the type of timber sale.';
COMMENT ON COLUMN harvest_sale.sale_volume IS E'The volume (m3) advertised for the sale; or, if not advertised, the volume offered for sale.';
COMMENT ON COLUMN harvest_sale.salvage_ind IS E'Indicates if the timber sale is for salvage wood.';
COMMENT ON COLUMN harvest_sale.sb_fund_ind IS E'See FTA data model for more details.';
COMMENT ON COLUMN harvest_sale.sold_sb_cat_code IS E'Describes the type of timber sale.';
COMMENT ON COLUMN harvest_sale.tender_opening_dt IS E'The advertised date that sealed tenders will be opened or the date the auction was held.';
COMMENT ON COLUMN harvest_sale.total_bidders IS E'Total number of applicants that bid on this sale. To be eventually replaced by a calculated field.';
COMMENT ON COLUMN harvest_sale.update_timestamp IS E'Timestamp of this data"s last update.';
COMMENT ON COLUMN harvest_sale.update_userid IS E'Id of user making last change to data.';


CREATE TABLE harvest_type_code (
	harvest_type_code varchar(1) NOT NULL,
	description varchar(120) NOT NULL,
