COMMENT ON COLUMN harvest_type_code.harvest_type_code IS E'Identifies the type of harvesting authority. Valid values include "Green", "Road", "Multi-Mark", and "Fort St. John".';
COMMENT ON COLUMN harvest_type_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE hauling_authority (
	timber_mark varchar(6) NOT NULL,
	forest_file_id varchar(10),
	marking_method_code varchar(1),
	marking_instrument_code varchar(1),
	revision_count integer NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	entry_userid varchar(30) NOT NULL,
	hauling_authority_guid uuid NOT NULL DEFAULT uuid_generate_v4()
) ;
COMMENT ON TABLE hauling_authority IS E'Information about the moving permission for a timber tenure. For some Road Permits and Licences to Cut, no moving permission is granted.';
COMMENT ON COLUMN hauling_authority.entry_timestamp IS E'Timestamp when the event information was entered.';
COMMENT ON COLUMN hauling_authority.entry_userid IS E'The unique user id of the resource who initially added the entry.';
COMMENT ON COLUMN hauling_authority.forest_file_id IS E'File identification assigned to Provincial Forest Use files. Assigned file number. Usually the Licence, Tenure or Private Mark number.';
COMMENT ON COLUMN hauling_authority.hauling_authority_guid IS E'Contains Global Unique Identifier for Hauling Authority';
COMMENT ON COLUMN hauling_authority.marking_instrument_code IS E'Code indicating the means of marking the timber for identification to the timber mark, eg., hammer, paint.';
COMMENT ON COLUMN hauling_authority.marking_method_code IS E'Code indicating the method of marking the truckload of harvested timber.';
COMMENT ON COLUMN hauling_authority.revision_count IS E'A count of the number of times an entry in the entity has been modified. Used to validate if the current information displayed on a user"s web browser is the most current.';
COMMENT ON COLUMN hauling_authority.timber_mark IS E'See FTA data model for details.';
COMMENT ON COLUMN hauling_authority.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN hauling_authority.update_userid IS E'The userid of the individual who last updated this information.';


CREATE TABLE height_class_code (
