CREATE TABLE opening (
	opening_id bigint NOT NULL,
	geo_district_no bigint NOT NULL,
	admin_district_no bigint NOT NULL,
	mapsheet_grid varchar(3),
	mapsheet_letter varchar(1),
	mapsheet_square varchar(3),
	mapsheet_quad varchar(1),
	mapsheet_sub_quad varchar(1),
	opening_number varchar(4),
	opening_locn_name varchar(30),
	open_category_code varchar(7) NOT NULL,
	licensee_opening_id varchar(30),
	tsb_number_code varchar(3),
	opening_status_code varchar(3) NOT NULL,
	org_unit_no bigint,
	dist_admin_zone varchar(2),
	max_allow_permnt_access_pct decimal(3,1),
	prev_age_class_code varchar(1),
	prev_site_index integer,
	prev_site_index_source_code varchar(1),
	prev_height_class_code varchar(1),
	prev_site_class_code varchar(1),
	prev_stocking_class_code varchar(1),
	prev_stocking_status_code varchar(3),
	prev_tree_spp1_code varchar(8),
	prev_tree_spp2_code varchar(8),
	app_ent_by_userid varchar(30),
	approve_date timestamp(0),
	amendment_ind varchar(1),
	results_submission_id bigint,
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON TABLE opening IS E'The administrative boundary of an area of land on which silviculture activities are planned and completed.';
COMMENT ON COLUMN opening.admin_district_no IS E'Unique physical identifier for the storage of organizational unit codes for the Ministry of Forests" offices. Values stored here are for the computer"s use only, and are not to be used by people as "ministry codes".';
COMMENT ON COLUMN opening.amendment_ind IS E'A Y/N indicator, indicating if the prescription has been amended.';
COMMENT ON COLUMN opening.app_ent_by_userid IS E'The userid of the individual who entered the prescription information.';
COMMENT ON COLUMN opening.approve_date IS E'The date on which the prescription was approved.';
COMMENT ON COLUMN opening.dist_admin_zone IS E'The code for the district zone where the opening is located. Codes are unique within each district.';
COMMENT ON COLUMN opening.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN opening.entry_userid IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN opening.geo_district_no IS E'Unique physical identifier for the storage of organizational unit codes for the Ministry of Forests" offices. Values stored here are for the computer"s use only, and are not to be used by people as "ministry codes".';
COMMENT ON COLUMN opening.licensee_opening_id IS E'A unique identifier provided by the Licensee to identify the Opening Number.';
COMMENT ON COLUMN opening.mapsheet_grid IS E'NTG or BCGS grid. Values are 82, 83, 92, 93, 94, 95, 102, 103,104, 114.';
COMMENT ON COLUMN opening.mapsheet_letter IS E'BCGS/NTG letter. Values are A - P, and W.';
COMMENT ON COLUMN opening.mapsheet_quad IS E'Quadrant. Blank if NTG number value entered; 0 - 4 if BCGS number entered.';
COMMENT ON COLUMN opening.mapsheet_square IS E'BCGS NUMBER or NTG NUMBER and LETTER. BCGS NUMBER values are 1-100, and NTG NUMBER values are 1-16 with NTG LETTER values A - H, and W.';
COMMENT ON COLUMN opening.mapsheet_sub_quad IS E'Sub-Quadrant. Blank if NTG number value entered; 0 - 4 if BCGS number entered.';
COMMENT ON COLUMN opening.max_allow_permnt_access_pct IS E'Maximum percentage of the Total Area Under the Prescription that may be occupied by permanent access structures, including roads, landings, gravel pits, borrow pits or other.';
COMMENT ON COLUMN opening.open_category_code IS E'A code used to describe the category for the opening. The opening categories reference the governing applicable legislation and are determined by responsibility, opening origin, tenure type and prescription type.';
COMMENT ON COLUMN opening.opening_id IS E'System generated value uniquely identifying the opening.';
COMMENT ON COLUMN opening.opening_locn_name IS E'A descriptive name of the geographic location of the opening.';
COMMENT ON COLUMN opening.opening_number IS E'An unique identifier up to four characters long that describes the opening on a specified mapsheet.';
COMMENT ON COLUMN opening.opening_status_code IS E'A code indicating the status of the opening. Examples include but are not limited to DFT (draft) and APP (approved). A subset of the STATUS_CODE table.';
COMMENT ON COLUMN opening.org_unit_no IS E'Unique physical identifier for the storage of organizational unit codes for the Ministry of Forests" offices. Values stored here are for the computer"s use only, and are not to be used by people as "ministry codes".';
COMMENT ON COLUMN opening.prev_age_class_code IS E'The age classification of the primary tree species in the previous stand (for the area under the prescription).';
COMMENT ON COLUMN opening.prev_height_class_code IS E'The height clan code (or the primary tree species in the previous stand (for the area under the prescription).';
COMMENT ON COLUMN opening.prev_site_class_code IS E'This code indicates the quality of the land. Note: This column is not maintained by the system.';
COMMENT ON COLUMN opening.prev_site_index IS E'The site index for the primary tree species in the previous stand (for the area under the prescription).';
COMMENT ON COLUMN opening.prev_site_index_source_code IS E'The site index source code for the primary tree species in the previous stand (for the area under the opening).';
COMMENT ON COLUMN opening.prev_stocking_class_code IS E'The stocking class code for the primary tree species in the previous stand (for the area under the prescription).';
COMMENT ON COLUMN opening.prev_stocking_status_code IS E'A code to describe the previous stand type for the area under the prescription.';
COMMENT ON COLUMN opening.prev_tree_spp1_code IS E'The secondary tree species of Crown timber that was cut, damaged, destroyed or removed without authority';
COMMENT ON COLUMN opening.prev_tree_spp2_code IS E'The secondary tree species of Crown timber that was cut, damaged, destroyed or removed without authority';
COMMENT ON COLUMN opening.results_submission_id IS E'Used to link information about a data submission. A submission may have many rows of Silviculture (Form A), Activities (Form B), and Forest Cover/Milestones (Form C) information. This attribute will be receiving a system generated number from the Electronic Submission Framework.';
COMMENT ON COLUMN opening.revision_count IS E'A counter identifying the number of times this record as been modified. Used in the web page access to determine if the record as been modified since the data was first retrieved.';
COMMENT ON COLUMN opening.tsb_number_code IS E'The unique code identifying a Timber Supply Block.';
COMMENT ON COLUMN opening.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN opening.update_userid IS E'The USERID of the individual who last updated the information.';


CREATE TABLE opening_amendment_history (
	opening_id bigint NOT NULL,
	opening_amendment_number integer NOT NULL,
	amendment_userid varchar(30) NOT NULL,
	amendment_date timestamp(0) NOT NULL,
	submitted_by_userid varchar(30),
	submitted_date timestamp(0),
	app_ent_by_userid varchar(30),
	approve_date timestamp(0),
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON TABLE opening_amendment_history IS E'Shows history of amendments made to an opening. Keeps track of dates, times, user identification and user name.';
COMMENT ON COLUMN opening_amendment_history.amendment_date IS E'The date the amendment was first initiated.';
COMMENT ON COLUMN opening_amendment_history.amendment_userid IS E'The user ID of the person who initiates the amendment. Changes the status from APP to AMD.';
COMMENT ON COLUMN opening_amendment_history.app_ent_by_userid IS E'The userid of the individual who entered the prescription information.';
COMMENT ON COLUMN opening_amendment_history.approve_date IS E'The date that SP or amendment has been approved (or put into effect (for MoF SP"s).';
COMMENT ON COLUMN opening_amendment_history.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN opening_amendment_history.entry_userid IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN opening_amendment_history.opening_amendment_number IS E'A system generated value uniquely identifying an amendment.';
COMMENT ON COLUMN opening_amendment_history.opening_id IS E'System generated value uniquely identifying the opening.';
COMMENT ON COLUMN opening_amendment_history.revision_count IS E'Internal counter used by the system to avoid conflicting updates to the record.';
COMMENT ON COLUMN opening_amendment_history.submitted_by_userid IS E'The USERID of the person who submitted the amendment.';
COMMENT ON COLUMN opening_amendment_history.submitted_date IS E'The date and time the amendment was submitted.';
COMMENT ON COLUMN opening_amendment_history.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN opening_amendment_history.update_userid IS E'The USERID of the individual who last updated the information.';


CREATE TABLE opening_attachment (
	opening_attachment_file_id bigint NOT NULL,
	opening_id bigint NOT NULL,
	attachment_name varchar(50) NOT NULL,
	attachment_description varchar(120),
	mime_type_code varchar(3),
	attachment_data bytea NOT NULL,
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL,
	opening_attachment_guid uuid NOT NULL DEFAULT uuid_generate_v4()
) ;
COMMENT ON TABLE opening_attachment IS E'A file attached to an Opening for purpose of clarification or exhibit.';
COMMENT ON COLUMN opening_attachment.attachment_data IS E'The attached file.';
COMMENT ON COLUMN opening_attachment.attachment_description IS E'Short description of the contents of the file.';
COMMENT ON COLUMN opening_attachment.attachment_name IS E'File name, not including the path, of the attached file at the time it was uploaded.';
COMMENT ON COLUMN opening_attachment.entry_timestamp IS E'Date and time that the file was attached to the Opening.';
COMMENT ON COLUMN opening_attachment.entry_userid IS E'User who attached the file to the Opening.';
COMMENT ON COLUMN opening_attachment.mime_type_code IS E'Multipurpose Internet Mail Extension (MIME) type is the method used by Web browsers to associate files of a certain type with helper applications that display files of that type.';
COMMENT ON COLUMN opening_attachment.opening_attachment_file_id IS E'Uniquely identifies the attached file.';
COMMENT ON COLUMN opening_attachment.opening_attachment_guid IS E'Unique identifier used to identify, save, retrieve or delete the Opening Attachment in the object storage. Saved as the ID within the metadata as well as the obfuscated file name';
COMMENT ON COLUMN opening_attachment.opening_id IS E'System generated value uniquely identifying the opening.';
COMMENT ON COLUMN opening_attachment.revision_count IS E'A counter identifying the number of times this record as been modified. Used in the web page access to determine if the record as been modified since the data was first retrieved.';
COMMENT ON COLUMN opening_attachment.update_timestamp IS E'Date and time that the attachment was last updated.';
COMMENT ON COLUMN opening_attachment.update_userid IS E'Userid of the user who last upated the attachment.';


CREATE TABLE opening_comment_link (
	opening_id bigint NOT NULL,
	silviculture_comment_id bigint NOT NULL
) ;
COMMENT ON COLUMN opening_comment_link.opening_id IS E'System generated value uniquely identifying the opening.';
COMMENT ON COLUMN opening_comment_link.silviculture_comment_id IS E'System generated value uniquely identifying the ISIS comment.';


CREATE TABLE opening_geometry (
	opening_id bigint NOT NULL,
	geometry geometry(GEOMETRY,3005) NOT NULL,
	feature_area decimal(11,4) NOT NULL,
	feature_perimeter decimal(11,4) NOT NULL,
	feature_class_skey bigint NOT NULL,
	capture_method_code varchar(30),
	data_source_code varchar(10),
	observation_date timestamp(0),
	data_quality_comment varchar(255),
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON TABLE opening_geometry IS E'The spatial representation for an opening, which is the administrative boundary of an area of land on which silviculture activities are planned and completed.';
COMMENT ON COLUMN opening_geometry.data_quality_comment IS E'Allows users to specify comments regarding the accuracy and general quality of the given feature"s data.';
COMMENT ON COLUMN opening_geometry.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN opening_geometry.entry_userid IS E'The userid of the individual who entered the information.';
COMMENT ON COLUMN opening_geometry.feature_area IS E'The area of the polygon pertaining to the Opening measured in square metres.';
COMMENT ON COLUMN opening_geometry.feature_class_skey IS E'Unique identifier of the feature class.';
COMMENT ON COLUMN opening_geometry.feature_perimeter IS E'The perimeter of the polygon pertaining to the Opening measured in metres.';
COMMENT ON COLUMN opening_geometry.geometry IS E'Spatial coordinate representation of the polygon related to the Opening.';
COMMENT ON COLUMN opening_geometry.observation_date IS E'Captures the date of observation for the given feature image.';
COMMENT ON COLUMN opening_geometry.opening_id IS E'System generated value uniquely identifying the opening.';
COMMENT ON COLUMN opening_geometry.revision_count IS E'A counter identifying the number of times this record as been modified. Used in the web page access to determine if the record as been modified since the data was first retrieved.';
COMMENT ON COLUMN opening_geometry.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN opening_geometry.update_userid IS E'The userid of the individual who last updated the information.';


CREATE TABLE opening_land_status (
	opening_id bigint NOT NULL,
	opening_land_status_date timestamp(0) NOT NULL,
	np_unn_area decimal(7,1) NOT NULL,
	np_nat_area decimal(7,1) NOT NULL,
	np_for_area decimal(7,1) NOT NULL,
	nsr_npl_area decimal(7,1) NOT NULL,
	nsr_nat_area decimal(7,1) NOT NULL,
	nsr_pl_area decimal(7,1) NOT NULL,
	sr_art_area decimal(7,1) NOT NULL,
	sr_nat_area decimal(7,1) NOT NULL,
	mat_area decimal(7,1) NOT NULL,
	fg_norm_area decimal(7,1),
	nc_br_area decimal(7,1) NOT NULL,
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON TABLE opening_land_status IS E'Identifies the land status of the entire opening on a given date. These attributes are derived from the FOREST_COVER table.';
COMMENT ON COLUMN opening_land_status.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN opening_land_status.entry_userid IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN opening_land_status.fg_norm_area IS E'The free growing area, expressed in hectares, of all polygons that have the "Free Growing" indicator set to "Y". is area is not double counted against the "SR" total.';
COMMENT ON COLUMN opening_land_status.mat_area IS E'The mature natural and artificial area , expressed in hectares. The Mature area field was added to the database in July 2000. Prior to July 2000 all Mature area was stored in the SR Artificial and SR Natural Area.';
COMMENT ON COLUMN opening_land_status.nc_br_area IS E'The non-commerical brush area, expressed in hectares. Area > 4 hectares and Ministry"s responsibility. Area has special significance in MLSIS and is not reflected in ISIS.';
COMMENT ON COLUMN opening_land_status.np_for_area IS E'The non-productive forested area, expressed in hectares.';
COMMENT ON COLUMN opening_land_status.np_nat_area IS E'The non-productive natural area, expressed in hectares.';
COMMENT ON COLUMN opening_land_status.np_unn_area IS E'The non-productive unnatural area, expressed in hectares.';
COMMENT ON COLUMN opening_land_status.nsr_nat_area IS E'The not sufficiently restocked natural area, expressed in hectares.';
COMMENT ON COLUMN opening_land_status.nsr_npl_area IS E'The not sufficiently restocked nonplantable area, expressed in hectares.';
COMMENT ON COLUMN opening_land_status.nsr_pl_area IS E'The not sufficiently restocked plantable area, expressed in hectares.';
COMMENT ON COLUMN opening_land_status.opening_id IS E'System generated value uniquely identifying the opening.';
COMMENT ON COLUMN opening_land_status.opening_land_status_date IS E'The date of the last status update.';
COMMENT ON COLUMN opening_land_status.revision_count IS E'A counter used to ensure data integrity. This item should be incremented during each update.';
COMMENT ON COLUMN opening_land_status.sr_art_area IS E'The sufficiently restocked artificial area, expressed in hectares.';
COMMENT ON COLUMN opening_land_status.sr_nat_area IS E'The sufficiently restocked natural area, expressed in hectares.';
COMMENT ON COLUMN opening_land_status.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN opening_land_status.update_userid IS E'The USERID of the individual who last updated the information.';


CREATE TABLE opening_status_code (
	opening_status_code varchar(3) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON COLUMN opening_status_code.description IS E'Full description of code value.';
COMMENT ON COLUMN opening_status_code.effective_date IS E'The date the value is available for use.';
COMMENT ON COLUMN opening_status_code.expiry_date IS E'The date the value is no longer available for use.';
COMMENT ON COLUMN opening_status_code.opening_status_code IS E'A code indicating the status of the prescription. Examples include but are not limited to DFT (draft) and APP (approved). A subset of the STATUS_CODE table.';
COMMENT ON COLUMN opening_status_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE open_category_code (
	open_category_code varchar(7) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON COLUMN open_category_code.description IS E'Full description of code value.';
COMMENT ON COLUMN open_category_code.effective_date IS E'The date the value is available for use.';
COMMENT ON COLUMN open_category_code.expiry_date IS E'The date the value is no longer available for use.';
COMMENT ON COLUMN open_category_code.open_category_code IS E'A code used to describe the category for the opening. The opening categories reference the governing applicable legislation and are determined by responsibility, opening origin, tenure type and prescription type.';
COMMENT ON COLUMN open_category_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE open_viewable_category (
	open_category_code varchar(7) NOT NULL,
	opening_status_code varchar(3),
	viewable_ind varchar(1) NOT NULL DEFAULT 'N'
) ;
COMMENT ON TABLE open_viewable_category IS E'OPEN_VIEWABLE_CATEGORY identifies the open categories that can be accessed by all licensees using the application. The system must use this stored information as a means to allow access for all users to openings that have an opening category with this designation enabled or openings in a Free Growing status. All other opening categories will continue to be restricted based on the current business rules.';
COMMENT ON COLUMN open_viewable_category.open_category_code IS E'A code used to describe the category for the opening. The opening categories reference the governing applicable legislation and are determined by responsibility, opening origin, tenure type and prescription type.';
COMMENT ON COLUMN open_viewable_category.opening_status_code IS E'A code indicating the status of the prescription. Examples include but are not limited to DFT (draft) and APP (approved). A subset of the STATUS_CODE table.';
COMMENT ON COLUMN open_viewable_category.viewable_ind IS E'Indicates whether the category is viewable for all users in RESULTS. Allowable values are "Y" and "N".';


CREATE TABLE org_unit (
	org_unit_no bigint NOT NULL,
	org_unit_code varchar(6) NOT NULL,
	org_unit_name varchar(100) NOT NULL,
	location_code varchar(3) NOT NULL,
	area_code varchar(3) NOT NULL,
	telephone_no varchar(7) NOT NULL,
	org_level_code varchar(1) NOT NULL,
	office_name_code varchar(2) NOT NULL,
	rollup_region_no bigint NOT NULL,
	rollup_region_code varchar(6) NOT NULL,
	rollup_dist_no bigint NOT NULL,
	rollup_dist_code varchar(6) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0)
) ;
COMMENT ON COLUMN org_unit.area_code IS E'The 3 digit numer that identifies each telephone service area in a country.';
COMMENT ON COLUMN org_unit.effective_date IS E'The date the Organizational Unit became effective as a valid Org Unit within the ministry"s organizational structure.';
COMMENT ON COLUMN org_unit.expiry_date IS E'The date the Organizational Unit is Obsoleted and is no longer an active Org Unit within the ministrys organizational structure. At the determination of Human Resources Branch, an expired Org Unit may be retained for historical purposed, or may be deleted.';
COMMENT ON COLUMN org_unit.location_code IS E'Unique identifier for one of the office locations within the Ministry of Forests.';
COMMENT ON COLUMN org_unit.office_name_code IS E'Represents an office within a specific organizational level.';
COMMENT ON COLUMN org_unit.org_level_code IS E'Represents the organizational level of the Ministry, and is one of: D - District Forestry Office, E - Executive, H - Headquarters Branch, R - Regional Forestry Office.';
COMMENT ON COLUMN org_unit.org_unit_code IS E'Identifies any office within the ministry. First character identifies Exec, HQ branch, Region, or District. Next two chars identify the office name; next two the section (HQ Branch) or program (Region or District); last char identifies the subsection.';
COMMENT ON COLUMN org_unit.org_unit_name IS E'The name or title of a ministry office or section; for example Kamloops Forest Region; Silviculture Branch; Kispiox Forest District Protection program.';
COMMENT ON COLUMN org_unit.org_unit_no IS E'Unique physical identifier for the storage of organizational unit codes for the Ministry of Forests" offices. Values stored here are for the computer"s use only, and are not to be used by people as "ministry codes".';
COMMENT ON COLUMN org_unit.rollup_dist_code IS E'Identifier designating the district to which the org unit is rolled into for reporting purposes.';
COMMENT ON COLUMN org_unit.rollup_dist_no IS E'Identifier designating the district to which the org unit is rolled into for reporting purposes. Values stored here are for the computers use only and are not to be used by people as "Ministry Codes".';
COMMENT ON COLUMN org_unit.rollup_region_code IS E'Identifier designating the region to which the org unit is rolled into for reporting purposes.';
COMMENT ON COLUMN org_unit.rollup_region_no IS E'Identifier designating the region to which the org unit is rolled into for reporting purposes. Values stored here are for the computers use only, and are not to be used by people as "Ministry Codes".';
COMMENT ON COLUMN org_unit.telephone_no IS E'The general office telephone number (excluding the area code) for a Ministry of Forests office.';
COMMENT ON COLUMN org_unit.update_timestamp IS E'The date and time of the last modification.';


CREATE TABLE payment_method_code (
	payment_method_code varchar(1) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON COLUMN payment_method_code.description IS E'Description of the code';
COMMENT ON COLUMN payment_method_code.effective_date IS E'Date the code is effective';
COMMENT ON COLUMN payment_method_code.expiry_date IS E'Date the code expires';
COMMENT ON COLUMN payment_method_code.payment_method_code IS E'The method of collecting the payment for the licence, such as "Cash" or "Automatic Invoice".';
COMMENT ON COLUMN payment_method_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE planting_rslt (
	activity_treatment_unit_id bigint NOT NULL,
	results_ind varchar(1) NOT NULL,
	plant_rslt_seq_no integer NOT NULL,
	silv_tree_species_code varchar(8) NOT NULL,
	number_planted bigint,
	planted_no_beyond_xfer_limit bigint,
	bid_price_per_tree decimal(5,4),
	seedlot_number varchar(5),
	veg_lot_id varchar(5),
	request_skey bigint,
	item_id varchar(1),
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL,
	climate_based_seed_xfer_ind varchar(1) NOT NULL DEFAULT 'N'
) ;
COMMENT ON TABLE planting_rslt IS E'Collects planned and actual information about a planting activity performed on an Activity Treatment Unit.';
COMMENT ON COLUMN planting_rslt.activity_treatment_unit_id IS E'System generated value uniquely identifying the Activity Treatment Unit.';
COMMENT ON COLUMN planting_rslt.bid_price_per_tree IS E'The anticipated (RESULTS IND = N) or actual (RESULTS IND = Y) bid price per tree for a planting contract.';
COMMENT ON COLUMN planting_rslt.climate_based_seed_xfer_ind IS E'CLIMATE BASED SEED TRANSFER - Indicates that the seedlings ''transferred beyond limit'' are based on the Ministry''s Climate Change Seed Policy.';
COMMENT ON COLUMN planting_rslt.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN planting_rslt.entry_userid IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN planting_rslt.item_id IS E'A unique identifier for each item within a request. It is alphabetically assigned (A, B,...Z).';
COMMENT ON COLUMN planting_rslt.number_planted IS E'The number of stems planted for each species within the ATU.';
COMMENT ON COLUMN planting_rslt.plant_rslt_seq_no IS E'Internal processing variable used to maintain order of the plant stock allotments for a specific ATU.';
COMMENT ON COLUMN planting_rslt.planted_no_beyond_xfer_limit IS E'IN THE OVERFLOW TABLE.';
COMMENT ON COLUMN planting_rslt.request_skey IS E'A surrogate key to uniquely identify each Spar Request.';
COMMENT ON COLUMN planting_rslt.results_ind IS E'Indicates whether or not the planting information is planning information (N) or the result of actual planting performed.';
COMMENT ON COLUMN planting_rslt.revision_count IS E'Internal counter used by the system to avoid conflicting updates to the record.';
COMMENT ON COLUMN planting_rslt.seedlot_number IS E'The unique number (key) assigned to a quantity of seed of a particular species and quality from a given location collected at a given time.';
COMMENT ON COLUMN planting_rslt.silv_tree_species_code IS E'The secondary tree species of Crown timber that was cut, damaged, destroyed or removed without authority';
COMMENT ON COLUMN planting_rslt.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN planting_rslt.update_userid IS E'The USERID of the individual who last updated the information.';
COMMENT ON COLUMN planting_rslt.veg_lot_id IS E'An identifier that is system generated by the system to uniquely identify this record.';


CREATE TABLE prov_forest_use (
	forest_file_id varchar(10) NOT NULL,
	file_status_st varchar(3),
	file_status_date timestamp(0),
	file_type_code varchar(3),
	forest_region bigint NOT NULL,
	bcts_org_unit bigint,
	sb_funded_ind varchar(1) NOT NULL DEFAULT 'N',
	district_admin_zone varchar(4),
	mgmt_unit_type varchar(1),
	mgmt_unit_id varchar(4),
	revision_count integer NOT NULL,
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	forest_tenure_guid uuid NOT NULL DEFAULT uuid_generate_v4()
) ;
COMMENT ON TABLE prov_forest_use IS E'General header information to identify the type of forest land use managed by the Ministry of Forests (eg., timber tenures, grazing tenures, recreation files, forest service roads, etc.). This is the main access point for examining land use information. Recommend that this entity be renamed to FOREST TENURE or FOREST FILE in the future (TF - this needs more analysis to determine what is indentified in this entity - licence, permit, etc and if all rights identified belong here).';
COMMENT ON COLUMN prov_forest_use.bcts_org_unit IS E'Unique physical identifier for the storage of organizational unit codes for the Ministry of Forests" offices. Values stored here are for the computer"s use only, and are not to be used by people as "ministry codes".';
COMMENT ON COLUMN prov_forest_use.district_admin_zone IS E'District Admin Zone is a free format field used internally by the districts to group files within a district for reporting purposes. Examples of use are setting the field to a geographic area or to a persons initials. Reports can be pulled off by District Admin Zone to aid work management. This field is not used by all districts.';
COMMENT ON COLUMN prov_forest_use.entry_timestamp IS E'Timestamp when the event information was entered.';
COMMENT ON COLUMN prov_forest_use.entry_userid IS E'The unique user id of the resource who initially added the entry.';
COMMENT ON COLUMN prov_forest_use.file_status_date IS E'The date on which the current forest land use status was set.';
COMMENT ON COLUMN prov_forest_use.file_status_st IS E'The current status of the forest land use, e.g. Pending - Planned, Harvesting - Suspended, Active. This is a subset of timber status code.';
COMMENT ON COLUMN prov_forest_use.file_type_code IS E'The FTAS code to indicate the type of file, and often synonymous with a tenure or a project.';
COMMENT ON COLUMN prov_forest_use.forest_file_id IS E'File identification assigned to Provincial Forest Use files. Assigned file number. Usually the Licence, Tenure or Private Mark number.';
COMMENT ON COLUMN prov_forest_use.forest_region IS E'Unique physical identifier for the storage of organizational unit codes for the Ministry of Forests" offices. Values stored here are for the computer"s use only, and are not to be used by people as "ministry codes".';
COMMENT ON COLUMN prov_forest_use.forest_tenure_guid IS E'A global unique identifier used to globally identified a Forest Tenure (aka, Forest File). It is used for exchanging data between external applications and services.';
COMMENT ON COLUMN prov_forest_use.mgmt_unit_id IS E'The administrative management unit identifier defined by the ministry for managing the forest tenure, eg. 41 - Dawson Creek TSA.';
COMMENT ON COLUMN prov_forest_use.mgmt_unit_type IS E'The unique identifier for the management unit type.';
COMMENT ON COLUMN prov_forest_use.revision_count IS E'A count of the number of times an entry in the entity has been modified. Used to validate if the current information displayed on a user"s web browser is the most current.';
COMMENT ON COLUMN prov_forest_use.sb_funded_ind IS E'Indicates if the harvest tenure is small business funded.';
COMMENT ON COLUMN prov_forest_use.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN prov_forest_use.update_userid IS E'The userid of the individual who last updated this information.';


CREATE TABLE quota_type_code (
	quota_type_code varchar(1) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON COLUMN quota_type_code.description IS E'Description of the code value';
COMMENT ON COLUMN quota_type_code.effective_date IS E'Date the code becomes effective';
COMMENT ON COLUMN quota_type_code.expiry_date IS E'Date the code expires';
COMMENT ON COLUMN quota_type_code.quota_type_code IS E'A Timber Supply Area will have volumes assigned to one or more partitions, which are in effect different timber type classifications, or geographic locations. Under each partition are the different apportionments. Examples of Partitions are Conventional';
COMMENT ON COLUMN quota_type_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE reforest_declare_type_code (
	reforest_declare_type_code varchar(3) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON COLUMN reforest_declare_type_code.description IS E'Description of the code value';
COMMENT ON COLUMN reforest_declare_type_code.effective_date IS E'Date the code becomes effective';
COMMENT ON COLUMN reforest_declare_type_code.expiry_date IS E'Date the code expires';
COMMENT ON COLUMN reforest_declare_type_code.reforest_declare_type_code IS E'Identifies the reforestation declaration type. E.g. Coniferous, Deciduos, Mix.';
COMMENT ON COLUMN reforest_declare_type_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE request_item (
	request_skey bigint NOT NULL,
	item_id varchar(1) NOT NULL,
	standard_workplan_skey bigint,
	vegetation_code varchar(8),
	sample_id varchar(5),
	family_lot_number varchar(13),
	rqst_sdlngs_rsrvd decimal(6,1) NOT NULL,
	rqst_sdlngs_srpls decimal(6,1) NOT NULL,
	cone_ind varchar(1),
	no_of_containers decimal(6,2),
	no_of_cones integer,
	cone_volume decimal(6,2),
	seed_quantity bigint,
	sample_elevation integer,
	shipto_cli_number varchar(8),
	shipto_cli_locn_cd varchar(2),
	required_by_date timestamp(0),
	latest_actioned_date timestamp(0),
	rcmnded_sowing_date timestamp(0),
	spcfied_sowing_date timestamp(0),
	tender_number varchar(7),
	tender_bid_group varchar(3),
	tender_issued_date timestamp(0),
	assigned_price decimal(6,2),
	nrsry_obligatn_ind varchar(1),
	nrsry_cli_number varchar(8),
	nrsry_cli_locn_cd varchar(2),
	facility_code varchar(2),
	extrct_cli_number varchar(8),
	extrct_cli_locn_cd varchar(2),
	authorization_code varchar(3),
	srv_blng_create_ts timestamp(0),
	sample_loc_desc varchar(30),
	interim_storge_loc varchar(50),
	authorized_by_prsn varchar(30),
	test_approved_userid varchar(30),
	test_approved_date timestamp(0),
	test_update_userid varchar(30),
	test_update_timestamp timestamp(0),
	test_comment varchar(2000),
	seedlot_qa_test_status_code varchar(3)
) ;
COMMENT ON TABLE request_item IS E'A particular item within a request for which services are being requested.';
COMMENT ON COLUMN request_item.assigned_price IS E'The assigned price assigned from the tendering process.';
COMMENT ON COLUMN request_item.authorization_code IS E'a code which represents the form by which authority for the request was received.';
COMMENT ON COLUMN request_item.authorized_by_prsn IS E'The name of the person who authorized the request.';
COMMENT ON COLUMN request_item.cone_ind IS E'An indicator which represents whether a sample is for cone ("Y" yes) or not ("N" no).';
COMMENT ON COLUMN request_item.cone_volume IS E'The total volume of cones, in hectolitres, that are part of the request item.';
COMMENT ON COLUMN request_item.extrct_cli_locn_cd IS E'A code to uniquely identify, within each client, the addresses of different divisions or locations at which the client operates. The location code is sequentially assigned starting with "00" for the client"s permanent address.';
COMMENT ON COLUMN request_item.extrct_cli_number IS E'A sequentially assigned number which uniquely identifies a Ministry client.';
COMMENT ON COLUMN request_item.facility_code IS E'A code which represents the type of facility that the seedlings will be grown in.';
COMMENT ON COLUMN request_item.family_lot_number IS E'The unique alphanumeric (key) assigned to a quantity of seed or cones of a particular species and quality from a given location collected at a given time.';
COMMENT ON COLUMN request_item.interim_storge_loc IS E'The location where the Request Item will be stored before it reaches the location where the services are to be performed.';
COMMENT ON COLUMN request_item.item_id IS E'A unique identifier for each item within a request. It is alphabetically assigned (A, B,...Z).';
COMMENT ON COLUMN request_item.latest_actioned_date IS E'The latest date (year, month and day) for which any changes can be applied to the requested item before it is scheduled to be actioned.';
COMMENT ON COLUMN request_item.no_of_cones IS E'The number of cones that are part of the request item if it is a sample.';
COMMENT ON COLUMN request_item.no_of_containers IS E'The number of containers (sacks of cones/bags of seed) that are part of the request item.';
COMMENT ON COLUMN request_item.nrsry_cli_locn_cd IS E'A code to uniquely identify, within each client, the addresses of different divisions or locations at which the client operates. The location code is sequentially assigned starting with "00" for the client"s permanent address.';
COMMENT ON COLUMN request_item.nrsry_cli_number IS E'A sequentially assigned number which uniquely identifies a Ministry client.';
COMMENT ON COLUMN request_item.nrsry_obligatn_ind IS E'An indicator which identifies whether or not a nursery assignment has been finalized and obligated.';
COMMENT ON COLUMN request_item.rcmnded_sowing_date IS E'The system"s recommended date in the given sowing year for which sowing should take place for a Seedling Request.';
COMMENT ON COLUMN request_item.request_skey IS E'A surrogate key to uniquely identify each Spar Request.';
COMMENT ON COLUMN request_item.required_by_date IS E'The date (year, month and day) on which the requestor would like the services completed.';
COMMENT ON COLUMN request_item.rqst_sdlngs_rsrvd IS E'The number, in thousands, of seedlings that were requested from an owner"s Reserve portion of a lot.';
COMMENT ON COLUMN request_item.rqst_sdlngs_srpls IS E'The number, in thousands, of seedlings that were requested from an owner"s Surplus portion of a lot.';
COMMENT ON COLUMN request_item.sample_elevation IS E'The representative elevation in meters where the sample originated.';
COMMENT ON COLUMN request_item.sample_id IS E'The identifier of a sample if it is not a seedlot.';
COMMENT ON COLUMN request_item.sample_loc_desc IS E'A free format text description of the location where the sample originated.';
COMMENT ON COLUMN request_item.seed_quantity IS E'The total volume of seed, in grams, that are part of the request item.';
COMMENT ON COLUMN request_item.shipto_cli_locn_cd IS E'A code to uniquely identify, within each client, the addresses of different divisions or locations at which the client operates. The location code is sequentially assigned starting with "00" for the client"s permanent address.';
COMMENT ON COLUMN request_item.shipto_cli_number IS E'A sequentially assigned number which uniquely identifies a Ministry client.';
COMMENT ON COLUMN request_item.spcfied_sowing_date IS E'The date in the given sowing year which is specified by the assigned nursery for which sowing should take place for a Seedling Request. This date will override the recommended sowing day.';
COMMENT ON COLUMN request_item.srv_blng_create_ts IS E'The time and date when service billing rows were created for the Request Item.';
COMMENT ON COLUMN request_item.standard_workplan_skey IS E'A surrogate key which uniquely identifies a standard workplan.';
COMMENT ON COLUMN request_item.tender_bid_group IS E'An identifier within a Tender Number which is assigned by Silviculture Branch which indicates the part of a Tender on which the request item has been assigned.';
COMMENT ON COLUMN request_item.tender_issued_date IS E'The date (year, month and day) the request item was assigned to the Tender.';
COMMENT ON COLUMN request_item.tender_number IS E'A number assigned by Silviculture Branch which indicates the tender on which the request item has been assigned.';
COMMENT ON COLUMN request_item.test_approved_date IS E'The userid of the user who approved the Request Test Services for processing at the Tree Seed Centre.';
COMMENT ON COLUMN request_item.test_approved_userid IS E'The date that the Request Test Services were approved for processing by the Tree Seed Centre.';
COMMENT ON COLUMN request_item.test_comment IS E'A general comment about the Request Test Services for the Request Item.';
COMMENT ON COLUMN request_item.test_update_timestamp IS E'The date and time that Request Test Services for the Request Item were last updated.';
COMMENT ON COLUMN request_item.test_update_userid IS E'The date that the Request Test Services were approved for processing by the Tree Seed Centre.';
COMMENT ON COLUMN request_item.vegetation_code IS E'A code which represents a species of tree or brush.';


CREATE TABLE results_audit_action_code (
	results_audit_action_code varchar(3) NOT NULL,
	description varchar(120) NOT NULL,
	effective_date timestamp(0) NOT NULL,
	expiry_date timestamp(0) NOT NULL,
	update_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON COLUMN results_audit_action_code.description IS E'Full description of code value.';
COMMENT ON COLUMN results_audit_action_code.effective_date IS E'The date the value is available for use.';
COMMENT ON COLUMN results_audit_action_code.expiry_date IS E'The date the value is no longer available for use.';
COMMENT ON COLUMN results_audit_action_code.results_audit_action_code IS E'The action code is a code associated with the action. I -- Insert';
COMMENT ON COLUMN results_audit_action_code.update_timestamp IS E'The date and time the value was last modified.';


CREATE TABLE results_audit_detail (
	results_audit_detail_id bigint NOT NULL,
	results_audit_event_id bigint NOT NULL,
	business_identifier varchar(30),
	table_name varchar(30) NOT NULL,
	column_name varchar(30) NOT NULL,
	old_value varchar(250),
	new_value varchar(250),
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON TABLE results_audit_detail IS E'Details each of the changes included during an EDT AUDIT EVENT. Shows the value of the modified column before and after the change.';
COMMENT ON COLUMN results_audit_detail.business_identifier IS E'Describes the business key for which data is being audited. Examples of business keys are Licensee ID, Standards Unit ID, Forest Cover Layer ID.';
COMMENT ON COLUMN results_audit_detail.column_name IS E'The name of the column that the data was modified in.';
COMMENT ON COLUMN results_audit_detail.entry_timestamp IS E'The date and time the data change occurred.';
COMMENT ON COLUMN results_audit_detail.entry_userid IS E'The userid of the person who changed the data.';
COMMENT ON COLUMN results_audit_detail.new_value IS E'If the data type was Character, the value of the field before the change.';
COMMENT ON COLUMN results_audit_detail.old_value IS E'The type of data that was modified. Would be one of, Character, Date or Number. This attribute will be used to determine which of the value fields contains the prior value of the element modified.';
COMMENT ON COLUMN results_audit_detail.results_audit_detail_id IS E'The unique identifier for the audit detail entry. Created through a sequence.';
COMMENT ON COLUMN results_audit_detail.results_audit_event_id IS E'A unique system generated number to use as the key for the table.';
COMMENT ON COLUMN results_audit_detail.table_name IS E'The name of the data base table that was modified.';


CREATE TABLE results_audit_event (
	results_audit_event_id bigint NOT NULL,
	opening_id bigint,
	standards_regime_id bigint,
	silviculture_project_id bigint,
	results_audit_action_code varchar(3) NOT NULL,
	action_date timestamp(0) NOT NULL,
	description varchar(2000),
	user_id varchar(30),
	email_sent_ind varchar(1),
	xml_submission_id bigint,
	opening_amendment_number integer,
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL
) ;
COMMENT ON TABLE results_audit_event IS E'Stores Audit Information such as openings submitted, activities submitted, changes in statuses, etc.';
COMMENT ON COLUMN results_audit_event.action_date IS E'The date the action occurred that is being logged.';
COMMENT ON COLUMN results_audit_event.description IS E'Description of the action that occurred. IE: Opening ID 8736636 submitted, or Opening status changed from SUB to APP.';
COMMENT ON COLUMN results_audit_event.email_sent_ind IS E'Indicator used to indicate if email was sent or not for this audit event.';
COMMENT ON COLUMN results_audit_event.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN results_audit_event.entry_userid IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN results_audit_event.opening_amendment_number IS E'A system generated value uniquely identifying an amendment.';
COMMENT ON COLUMN results_audit_event.opening_id IS E'System generated value uniquely identifying the opening.';
COMMENT ON COLUMN results_audit_event.results_audit_action_code IS E'The action code is a code associated with the action. I -- Insert';
COMMENT ON COLUMN results_audit_event.results_audit_event_id IS E'A unique system generated number to use as the key for the table.';
COMMENT ON COLUMN results_audit_event.silviculture_project_id IS E'A system generated key which uniquely identifies an occurrence of a Silviculture Project.';
COMMENT ON COLUMN results_audit_event.standards_regime_id IS E'Unique identifier for the STANDARD REGIME entry.';
COMMENT ON COLUMN results_audit_event.user_id IS E'The user id were the email was sent.';
COMMENT ON COLUMN results_audit_event.xml_submission_id IS E'The submission file ID that was given when the data was loaded.';


CREATE TABLE results_electronic_submission (
	results_submission_id bigint NOT NULL,
	submission_timestamp timestamp(0) NOT NULL,
	submitted_by varchar(30) NOT NULL,
	client_number varchar(8),
	client_locn_code varchar(2),
	org_unit_no bigint,
	user_filename varchar(50),
	user_reference varchar(240),
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON TABLE results_electronic_submission IS E'General information about a data submission file which has been imported through the Electronic Submission Framework.';
COMMENT ON COLUMN results_electronic_submission.client_locn_code IS E'A code to uniquely identify, within each client, the addresses of different divisions or locations at which the client operates. The location code is sequentially assigned starting with "00" for the client"s permanent address.';
COMMENT ON COLUMN results_electronic_submission.client_number IS E'Sequentially assigned number to identify a ministry client.';
COMMENT ON COLUMN results_electronic_submission.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN results_electronic_submission.entry_userid IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN results_electronic_submission.org_unit_no IS E'Unique physical identifier for the storage of organizational unit codes for the Ministry of Forests" offices. Values stored here are for the computer"s use only, and are not to be used by people as "ministry codes".';
COMMENT ON COLUMN results_electronic_submission.results_submission_id IS E'Used to link information about a data submission. A submission may have many rows of Silviculture (Form A), Activities (Form B), and Forest Cover/Milestones (Form C) information. This attribute will be receiving a system generated number from the Electronic Submission Framework.';
COMMENT ON COLUMN results_electronic_submission.revision_count IS E'A counter identifying the number of times this record as been modified. Used in the web page access to determine if the record as been modified since the data was first retrieved.';
COMMENT ON COLUMN results_electronic_submission.submission_timestamp IS E'Date and time the data submission was read.';
COMMENT ON COLUMN results_electronic_submission.submitted_by IS E'The USERID of the person logged on when the submission was made.';
COMMENT ON COLUMN results_electronic_submission.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN results_electronic_submission.update_userid IS E'The USERID of the individual who last updated the information.';
COMMENT ON COLUMN results_electronic_submission.user_filename IS E'The file name identified by the licensee/Ministry when submitting the data through the Electronic Submission Framework.';
COMMENT ON COLUMN results_electronic_submission.user_reference IS E'A reference that the user can optionally supply. This field can be used by the user to search on for outstanding submissions.';


CREATE TABLE sale_method_code (
