CREATE TABLE silviculture_comment (
	silviculture_comment_id bigint NOT NULL,
	comment_date timestamp(0) NOT NULL,
	silv_comment_source_code varchar(4) NOT NULL,
	silv_comment_type_code varchar(8) NOT NULL,
	comment_text varchar(2000) NOT NULL,
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON TABLE silviculture_comment IS E'Stores general comments for the ISIS system, and is attached to one of: Opening, ATU, Project.';
COMMENT ON COLUMN silviculture_comment.comment_date IS E'Date which the comment is created.';
COMMENT ON COLUMN silviculture_comment.comment_text IS E'A long, variable length text field for entering comment text.';
COMMENT ON COLUMN silviculture_comment.entry_timestamp IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN silviculture_comment.entry_userid IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN silviculture_comment.revision_count IS E'A counter identifying the number of times this record as been modified. Used in the web page access to determine if the record as been modified since the data was first retrieved.';
COMMENT ON COLUMN silviculture_comment.silv_comment_source_code IS E'Code indicating source of comment. Examples include but are not limited to: OPEN, PHSP, RSLT, PLAN.';
COMMENT ON COLUMN silviculture_comment.silv_comment_type_code IS E'Code indicating type of comment. Examples include but are not limited to: MGMNTOBJ, RECREATN, LNDSCAPE, WILDLIFE, FISHWATR, FIRE, SP, SURVEY.';
COMMENT ON COLUMN silviculture_comment.silviculture_comment_id IS E'System generated value uniquely identifying the ISIS comment.';
COMMENT ON COLUMN silviculture_comment.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN silviculture_comment.update_userid IS E'The USERID of the individual who last update this information.';


CREATE TABLE silviculture_project (
	silviculture_project_id bigint NOT NULL,
	silv_base_code varchar(2) NOT NULL,
	fiscal_year smallint NOT NULL,
	org_unit_no bigint NOT NULL,
	project_sequence varchar(4) NOT NULL,
	project_location varchar(30),
	silv_project_status_code varchar(3) NOT NULL,
	client_number varchar(8),
	start_date timestamp(0) NOT NULL,
	view_date timestamp(0),
	coordinator_userid varchar(30),
	overhead_cost decimal(9,2),
	crew_contract_hire_code varchar(1),
	dist_admin_zone varchar(2),
	unit_bid_code varchar(3) NOT NULL,
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON TABLE silviculture_project IS E'A administrative instrument that allows Ministry staff to plan and execute contracts for silviculture treatments. Collects the general project information recorded during Planting, Site Preparation, Pruning and Survey activities';
COMMENT ON COLUMN silviculture_project.client_number IS E'Sequentially assigned number to identify a ministry client.';
COMMENT ON COLUMN silviculture_project.coordinator_userid IS E'The userid of the contract coordinator for the project.';
COMMENT ON COLUMN silviculture_project.crew_contract_hire_code IS E'A code to indicate the tendering process used to select a contractor.';
COMMENT ON COLUMN silviculture_project.dist_admin_zone IS E'The administrative zone for a district.';
COMMENT ON COLUMN silviculture_project.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN silviculture_project.entry_userid IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN silviculture_project.fiscal_year IS E'The fiscal year in which the project commenced, or, in the case of a planned project, is expected to commence. A component of the concatenated business key which consists of: SILV BASE CODE FISCAL YEAR ORG UNIT CODE PROJECT SEQUENCE e.g. PL2006DCR035A';
COMMENT ON COLUMN silviculture_project.org_unit_no IS E'Unique physical identifier for the storage of organizational unit codes for the Ministry of Forests" offices. Values stored here are for the computer"s use only, and are not to be used by people as "ministry codes".';
COMMENT ON COLUMN silviculture_project.overhead_cost IS E'The planned or actual total overhead costs associated with the project.';
COMMENT ON COLUMN silviculture_project.project_location IS E'A comment regarding the general location of the project.';
COMMENT ON COLUMN silviculture_project.project_sequence IS E'A one to four character identifier that is meaningful to the user and assists in distinguishing one Project from another within the same SILV BASE CODE, FISCAL YEAR, ORG UNIT CODE. A component of the concatenated business key which consists of: SILV BASE CODE FISCAL YEAR ORG UNIT CODE PROJECT SEQUENCE e.g. PL2006DCR035A';
COMMENT ON COLUMN silviculture_project.revision_count IS E'A counter identifying the number of times this record has been modified. Used in the web page access to determine if the record has been modified since the data was first retrieved.';
COMMENT ON COLUMN silviculture_project.silv_base_code IS E'Identifies a primary category of Silviculture Activity.';
COMMENT ON COLUMN silviculture_project.silv_project_status_code IS E'A code to indicate the current status of a SILVICULTURE PROJECT.';
COMMENT ON COLUMN silviculture_project.silviculture_project_id IS E'A system generated key which uniquely identifies an occurrence of a Silviculture Project.';
COMMENT ON COLUMN silviculture_project.start_date IS E'The date that the project actually commenced, or, in the case of a planned project, the date it is currently scheduled to commence.';
COMMENT ON COLUMN silviculture_project.unit_bid_code IS E'A code to denote the unit basis on which work was tendered (e.g. days, hectares). Subset of UNIT OF MEASURE CODE.';
COMMENT ON COLUMN silviculture_project.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN silviculture_project.update_userid IS E'The USERID of the individual who last updated the information.';
COMMENT ON COLUMN silviculture_project.view_date IS E'The planned or actual date of viewing the contract particulars.';


