CREATE TABLE silv_relief_application (
	silv_relief_application_id bigint NOT NULL,
	silv_relief_application_no smallint NOT NULL,
	activity_treatment_unit_id bigint NOT NULL,
	silv_relief_appl_status_code varchar(3) NOT NULL,
	pre_event_forest_cover_date timestamp(0) NOT NULL,
	post_event_forest_cover_date timestamp(0) NOT NULL,
	amendment_opening_id bigint,
	opening_amendment_number integer,
	obligation_relief_ind varchar(1) NOT NULL,
	obligation_met_ind varchar(1) NOT NULL,
	cause_damage_ind varchar(1) NOT NULL,
	officially_induced_error_ind varchar(1) NOT NULL,
	due_diligence_ind varchar(1) NOT NULL,
	authorization_checked_ind varchar(1) NOT NULL,
	submitted_date timestamp(0),
	submitted_by_userid varchar(30),
	approved_date timestamp(0),
	approved_by_userid varchar(30),
	reject_comment varchar(2000),
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON TABLE silv_relief_application IS E'Application for relief from obligation or montary reembursement for damage done by natural disasters etc.';
COMMENT ON COLUMN silv_relief_application.activity_treatment_unit_id IS E'System generated value uniquely identifying the Activity Treatment Unit.';
COMMENT ON COLUMN silv_relief_application.amendment_opening_id IS E'System generated value uniquely identifying the opening.';
COMMENT ON COLUMN silv_relief_application.approved_by_userid IS E'The userid of the individual who approved the application.';
COMMENT ON COLUMN silv_relief_application.approved_date IS E'The date and time the application was approved.';
COMMENT ON COLUMN silv_relief_application.authorization_checked_ind IS E'Indicates if the submitter is authorized by the holder of these silviculture obligations to make this submission and confirms their appropriate professional designation to make this submission.';
COMMENT ON COLUMN silv_relief_application.cause_damage_ind IS E'Indicates if the Obligation holder did not cause or contribute to the cause of the damage.';
COMMENT ON COLUMN silv_relief_application.due_diligence_ind IS E'Indicates if the Obligation holder exercised due diligence in relation to the cause of the damage.';
COMMENT ON COLUMN silv_relief_application.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN silv_relief_application.entry_userid IS E'The userid of the individual who entered the information.';
COMMENT ON COLUMN silv_relief_application.obligation_met_ind IS E'Indicates if the Obligation cannot be met without significant extra expense due to the damaging event.';
COMMENT ON COLUMN silv_relief_application.obligation_relief_ind IS E'Denotes whether this application is applying for full relief from obligations.';
COMMENT ON COLUMN silv_relief_application.officially_induced_error_ind IS E'Indicates if the Obligation holder contributed to the cause of the damage but only as a result of an officially induced error.';
COMMENT ON COLUMN silv_relief_application.opening_amendment_number IS E'A system generated value uniquely identifying an amendment.';
COMMENT ON COLUMN silv_relief_application.post_event_forest_cover_date IS E'Most recent observation date after the damaging event demonstrating the current forest condition leading to the relief request.';
COMMENT ON COLUMN silv_relief_application.pre_event_forest_cover_date IS E'Most recent observation date describing the forest condition prior to the event.';
COMMENT ON COLUMN silv_relief_application.reject_comment IS E'If the application is rejected, a reason must be provided.';
COMMENT ON COLUMN silv_relief_application.revision_count IS E'A counter identifying the number of times this record as been modified. Used in the web page access to determine if the record as been modified since the data was first retrieved.';
COMMENT ON COLUMN silv_relief_application.silv_relief_appl_status_code IS E'Code identifying the status of the relief application.';
COMMENT ON COLUMN silv_relief_application.silv_relief_application_id IS E'Uniquely identifies an Application for Relief from obligations.';
COMMENT ON COLUMN silv_relief_application.silv_relief_application_no IS E'The sequetial number given to the relief application baed on the number of application submitted for the opening only.';
COMMENT ON COLUMN silv_relief_application.submitted_by_userid IS E'The userid of the individual who submitted the application.';
COMMENT ON COLUMN silv_relief_application.submitted_date IS E'The date and time the application was submitted.';
COMMENT ON COLUMN silv_relief_application.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN silv_relief_application.update_userid IS E'The userid of the individual who last updated the information.';


