	regen_obligation_ind varchar(1) NOT NULL,
	no_regen_early_offset_yrs smallint,
	no_regen_late_offset_yrs smallint,
	free_growing_early_offset_yrs smallint,
	free_growing_late_offset_yrs smallint,
	approved_by_userid varchar(30),
	approved_date timestamp(0),
	submitted_by_userid varchar(30),
	submitted_date timestamp(0),
	effective_date timestamp(0),
	expiry_date timestamp(0),
	additional_standards varchar(4000),
	reject_note varchar(2000),
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON COLUMN standards_regime.additional_standards IS E'A free-format text field to allow for the definition of additional standards.';
COMMENT ON COLUMN standards_regime.approved_by_userid IS E'The userid of the resource that approved the standard regime definition.';
COMMENT ON COLUMN standards_regime.approved_date IS E'The date the standard regime definition was approved.';
COMMENT ON COLUMN standards_regime.effective_date IS E'The date upon which this standard goes into effect.';
COMMENT ON COLUMN standards_regime.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN standards_regime.entry_userid IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN standards_regime.expiry_date IS E'The date this standard is no longer available for use by new openings.';
COMMENT ON COLUMN standards_regime.free_growing_early_offset_yrs IS E'The minimum or early growth number of years.';
COMMENT ON COLUMN standards_regime.free_growing_late_offset_yrs IS E'The maximum or late years of tree growth.';
COMMENT ON COLUMN standards_regime.geographic_description IS E'A description of the georgraphic area for which standard are being set.';
COMMENT ON COLUMN standards_regime.mof_default_standard_ind IS E'Indicates whether the Stocking Standard was created by District, Region, or Headquarters staff.';
COMMENT ON COLUMN standards_regime.no_regen_early_offset_yrs IS E'The minimum or early regen number of years.';
COMMENT ON COLUMN standards_regime.no_regen_late_offset_yrs IS E'The maximum or late years of regen growth.';
COMMENT ON COLUMN standards_regime.regen_delay_offset_yrs IS E'The number of years allowed for the regeneration of tree growth.';
COMMENT ON COLUMN standards_regime.regen_obligation_ind IS E'A yes/no indicator indicating if this standard is for a regeneration obligation..';
COMMENT ON COLUMN standards_regime.reject_note IS E'A free-format text field allowing for the documentation of why a submitted standard was rejected.';
COMMENT ON COLUMN standards_regime.revision_count IS E'Internal counter used by the system to avoid conflicting updates to the record.';
COMMENT ON COLUMN standards_regime.silv_statute_code IS E'A legislative statute based on a current Act, Regulation or policy';
COMMENT ON COLUMN standards_regime.standards_objective IS E'Describes the objective of the standards.';
COMMENT ON COLUMN standards_regime.standards_regime_id IS E'Unique identifier for the STANDARD REGIME entry.';
COMMENT ON COLUMN standards_regime.standards_regime_name IS E'A descriptive name of the type of STANDARD being tracked.';
COMMENT ON COLUMN standards_regime.standards_regime_status_code IS E'The status of the STANDARD REGIME. Starts out as submitted and must be approved by an authorized resource prior to being used for a submission.';
COMMENT ON COLUMN standards_regime.submitted_by_userid IS E'The userid of the resource that submitted the standard regime definition.';
COMMENT ON COLUMN standards_regime.submitted_date IS E'The date and time the standard regime definition was submitted.';
COMMENT ON COLUMN standards_regime.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN standards_regime.update_userid IS E'The USERID of the individual who last updated the information.';


