CREATE TABLE stocking_layer (
	stocking_layer_id bigint NOT NULL,
	stocking_standard_unit_id bigint NOT NULL,
	opening_id bigint NOT NULL,
	stocking_layer_code varchar(2) NOT NULL,
	target_stocking integer,
	residual_basal_area integer,
	min_horizontal_distance decimal(3,1),
	min_pref_stocking_standard integer,
	min_post_spacing bigint,
	min_stocking_standard integer,
	max_post_spacing bigint,
	max_conifer bigint,
	hght_relative_to_comp integer,
	tree_size_unit_code varchar(3),
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL
) ;
COMMENT ON TABLE stocking_layer IS E'Details the standards unit layer information.';
COMMENT ON COLUMN stocking_layer.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN stocking_layer.entry_userid IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN stocking_layer.hght_relative_to_comp IS E'The height of the tree relative to competing vegetation, within a radius of one metre of the tree trunk, that must be met by a healthy well spaced tree of a preferred or acceptable species at FG.';
COMMENT ON COLUMN stocking_layer.max_conifer IS E'The maximum allowable stand density of total countable conifers in order to achieve free growing status.';
COMMENT ON COLUMN stocking_layer.max_post_spacing IS E'The maximum number of well spaced coniferous trees per hectare to be retained following maximum density spacing.';
COMMENT ON COLUMN stocking_layer.min_horizontal_distance IS E'Te minimum allowable horizontal distance in metres between trees of the preferred and acceptable species, which is required to be considered to be well-spaced.';
COMMENT ON COLUMN stocking_layer.min_post_spacing IS E'The minimum number of well spaced coniferous trees per hectare to be retained following maximum density spacing.';
COMMENT ON COLUMN stocking_layer.min_pref_stocking_standard IS E'The minimum number of well spaced trees per hectare, of preferred species only, that must be present for the standards unit to be considered SR or FG.';
COMMENT ON COLUMN stocking_layer.min_stocking_standard IS E'The minimum number of well spaced trees per hectare (of preferred and acceptable species) that must be present for the standards unit to be considered SR or FG.';
COMMENT ON COLUMN stocking_layer.opening_id IS E'System generated value uniquely identifying the opening.';
COMMENT ON COLUMN stocking_layer.residual_basal_area IS E'The residual basal area to be left after harvesting in m2/ha. Understood to refer to the mature layer only.';
COMMENT ON COLUMN stocking_layer.revision_count IS E'A counter identifying the number of times this record as been modified. Used in the web page access to determine if the record as been modified since the data was first retrieved.';
COMMENT ON COLUMN stocking_layer.stocking_layer_code IS E'A code that identifies the forested layer within the Standards Unit to which the preferred/acceptable species information relates.';
COMMENT ON COLUMN stocking_layer.stocking_layer_id IS E'The unique identifier for the STOCKING LAYER';
COMMENT ON COLUMN stocking_layer.stocking_standard_unit_id IS E'System generated value uniquely identifing the stocking standards.';
COMMENT ON COLUMN stocking_layer.target_stocking IS E'The number of well spaced (preferred or acceptable) trees per hectare that will, in normal circumstances, produce an optimal FG crop. Target stocking standards are the standards which silviculture activities are aimed at achieving.';
COMMENT ON COLUMN stocking_layer.tree_size_unit_code IS E'The unit of measure used for height relative to competition. Expressed either in number of centimetres above vegetation (cm), or percentage above vegetation (%).';
COMMENT ON COLUMN stocking_layer.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN stocking_layer.update_userid IS E'The USERID of the individual who last updated the information.';


