CREATE TABLE stocking_milestone (
	stocking_standard_unit_id bigint NOT NULL,
	silv_milestone_type_code varchar(3) NOT NULL,
	results_submission_id bigint,
	declared_date timestamp(0),
	declared_userid varchar(30),
	declare_ind varchar(1),
	declaration_submitted_date timestamp(0),
	early_offset_years smallint,
	late_offset_years smallint,
	due_early_date timestamp(0),
	due_late_date timestamp(0),
	entry_userid varchar(30) NOT NULL,
	entry_timestamp timestamp(0) NOT NULL,
	update_userid varchar(30) NOT NULL,
	update_timestamp timestamp(0) NOT NULL,
	revision_count integer NOT NULL,
	extent_feasible_declared_ind varchar(1) NOT NULL DEFAULT 'N'
) ;
COMMENT ON TABLE stocking_milestone IS E'Silviculture milestones that have been achieved for a specific area (SU - Standards Unit). The milestones that are currently tracked are post harvest, regeneration and free growing.';
COMMENT ON COLUMN stocking_milestone.declaration_submitted_date IS E'The date that the Milestone declaration was recorded electronically at the Ministry.';
COMMENT ON COLUMN stocking_milestone.declare_ind IS E'A system generated flag "Y/N" which is determined from the polygons which comprise the Standards Unit. If all polygons within the SU are declared free growing then "Y" is displayed. If all SU"s are declared FG then the opening can be declared FG. A Y/N flag indicating whether or not the given Standards unit within the prescription has been declared regenerated.';
COMMENT ON COLUMN stocking_milestone.declared_date IS E'The date that the Stocking Standard achieved the Milestone, not necessarily when it was recorded electronically at the Ministry. Previous descriptions from IDD: - The date the post harvest information was declared. - The date on which the Standards Unit was declared regenerated. - An enterable date at which the Standards Unit was declared free growing. This date cannot be in the future. The last date of declaration for all SU"s will become the opening free growing date.';
COMMENT ON COLUMN stocking_milestone.declared_userid IS E'The userid of the resource who declared the post harvest information.';
COMMENT ON COLUMN stocking_milestone.due_early_date IS E'The earliest year the milestone is to be declared.';
COMMENT ON COLUMN stocking_milestone.due_late_date IS E'The latest year the milestone is to be declared.';
COMMENT ON COLUMN stocking_milestone.early_offset_years IS E'The number of years, after the start of harvest, when free-growing may first be declared.';
COMMENT ON COLUMN stocking_milestone.entry_timestamp IS E'The date and time the information was entered.';
COMMENT ON COLUMN stocking_milestone.entry_userid IS E'The USERID of the individual who entered the information.';
COMMENT ON COLUMN stocking_milestone.extent_feasible_declared_ind IS E'DECLARED TO THE EXTENT PRACTICABLE - Indicator for Milestones that do not meet applicable Stocking Standards.';
COMMENT ON COLUMN stocking_milestone.late_offset_years IS E'The number of years, after the start of harvest, by which free-growing must be achieved.';
COMMENT ON COLUMN stocking_milestone.results_submission_id IS E'Used to link information about a data submission. A submission may have many rows of Silviculture (Form A), Activities (Form B), and Forest Cover/Milestones (Form C) information. This attribute will be receiving a system generated number from the Electronic Submission Framework.';
COMMENT ON COLUMN stocking_milestone.revision_count IS E'Internal counter used by the system to avoid conflicting updates to the record.';
COMMENT ON COLUMN stocking_milestone.silv_milestone_type_code IS E'A means of identifying if the associated milestone is for post harvest, regeneration or free growing.';
COMMENT ON COLUMN stocking_milestone.stocking_standard_unit_id IS E'System generated value uniquely identifing the stocking standards.';
COMMENT ON COLUMN stocking_milestone.update_timestamp IS E'The date and time of the last update.';
COMMENT ON COLUMN stocking_milestone.update_userid IS E'The USERID of the individual who last updated the information.';


