COMMENT ON COLUMN tree_cover_pattern_code.tree_cover_pattern_code IS E'The dispursion of trees over the area.';
COMMENT ON COLUMN tree_cover_pattern_code.update_timestamp IS E'The date and time the value was last modified';


CREATE TABLE tree_size_unit_code (
	tree_size_unit_code varchar(3) NOT NULL,
